import React, { useEffect, useRef } from 'react';

interface Voto {
  id: number;
  nombre: string;
  prediccion: 'varon' | 'nina';
  comentario?: string;
  foto_url?: string;
  fecha_voto: string;
}

interface TreeVisualizationProps {
  votos: Voto[];
}

const TreeVisualization: React.FC<TreeVisualizationProps> = ({ votos }) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);

  // Función para dibujar hoja SVG
  const drawLeaf = (ctx: CanvasRenderingContext2D, x: number, y: number, size: number, angle: number, color: string) => {
    ctx.save();
    ctx.translate(x, y);
    ctx.rotate(angle);
    ctx.scale(size / 76, size / 76); // Escalar basado en el viewBox del SVG

    // Dibujar la forma de la hoja usando el path del SVG
    ctx.fillStyle = color;
    ctx.beginPath();

    // Path del SVG convertido a coordenadas de canvas
    ctx.moveTo(57, 22.1667);
    ctx.bezierCurveTo(49.0833, 28.5, 52.25, 31.6667, 50.6667, 36.4167);
    ctx.bezierCurveTo(49.0833, 41.1667, 50.6667, 42.75, 44.3333, 47.5);
    ctx.bezierCurveTo(36.2227, 53.583, 29.2917, 49.875, 29.2917, 49.875);
    ctx.bezierCurveTo(27.7083, 51.4583, 21.7708, 54.2292, 21.7708, 54.2292);
    ctx.lineTo(20.0143, 50.7285);
    ctx.bezierCurveTo(26.3476, 49.1452, 27.7083, 47.1042, 27.7083, 47.1042);
    ctx.bezierCurveTo(26.125, 43.9375, 23.75, 38, 28.5, 30.0833);
    ctx.bezierCurveTo(33.25, 22.1667, 57, 22.1667, 57, 22.1667);
    ctx.closePath();
    ctx.fill();

    // Agregar sombra interna para dar profundidad
    ctx.fillStyle = 'rgba(0, 0, 0, 0.1)';
    ctx.beginPath();
    ctx.moveTo(30.0833, 44.3333);
    ctx.lineTo(31.6667, 45.9167);
    ctx.lineTo(31.6667, 45.9167);
    ctx.bezierCurveTo(34.8333, 44.3333, 36.4167, 39.5833, 36.4167, 39.5833);
    ctx.bezierCurveTo(38, 34.8333, 45.9167, 26.9167, 45.9167, 26.9167);
    ctx.closePath();
    ctx.fill();

    ctx.restore();
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const isMobile = window.innerWidth <= 768;

    // Configurar canvas
    canvas.width = canvas.offsetWidth;
    canvas.height = canvas.offsetHeight;
    const width = canvas.width;
    const height = canvas.height;

    // Limpiar canvas
    ctx.clearRect(0, 0, width, height);

    // Dibujar cielo con gradiente más suave
    const skyGradient = ctx.createLinearGradient(0, 0, 0, height * 0.7);
    skyGradient.addColorStop(0, '#87CEEB');
    skyGradient.addColorStop(0.5, '#B0E0E6');
    skyGradient.addColorStop(1, '#98FB98');
    ctx.fillStyle = skyGradient;
    ctx.fillRect(0, 0, width, height);

    // Dibujar suelo
    const groundGradient = ctx.createLinearGradient(0, height * 0.7, 0, height);
    groundGradient.addColorStop(0, '#98FB98');
    groundGradient.addColorStop(1, '#228B22');
    ctx.fillStyle = groundGradient;
    ctx.fillRect(0, height * 0.7, width, height * 0.3);

    // Dibujar nubes decorativas (detrás del árbol)
    ctx.fillStyle = 'rgba(255, 255, 255, 0.8)';
    ctx.shadowColor = 'rgba(255, 255, 255, 0.5)';
    ctx.shadowBlur = 10;

    // Nube izquierda
    ctx.beginPath();
    ctx.arc(width * 0.15, height * 0.08, 35, 0, 2 * Math.PI);
    ctx.arc(width * 0.15 + 30, height * 0.08, 28, 0, 2 * Math.PI);
    ctx.arc(width * 0.15 + 55, height * 0.08, 22, 0, 2 * Math.PI);
    ctx.arc(width * 0.15 + 20, height * 0.08 - 15, 25, 0, 2 * Math.PI);
    ctx.fill();

    // Nube derecha
    ctx.beginPath();
    ctx.arc(width * 0.85, height * 0.12, 30, 0, 2 * Math.PI);
    ctx.arc(width * 0.85 + 25, height * 0.12, 25, 0, 2 * Math.PI);
    ctx.arc(width * 0.85 + 45, height * 0.12, 18, 0, 2 * Math.PI);
    ctx.arc(width * 0.85 + 15, height * 0.12 - 12, 20, 0, 2 * Math.PI);
    ctx.fill();

    ctx.shadowBlur = 0;

    // Cargar y dibujar la imagen del árbol
    const treeImage = new Image();
    treeImage.onload = () => {
      // Calcular dimensiones del árbol centrado
      const treeSize = isMobile ? 200 : 300;
      const treeX = (width / 2) - (treeSize / 2);
      const treeY = isMobile ? height - 280 : height - 380;

      // Dibujar la imagen del árbol
      ctx.drawImage(treeImage, treeX, treeY, treeSize, treeSize);

      // Después de dibujar el árbol, dibujar las hojas
      drawLeavesAfterTree();
    };

    // Función para dibujar las hojas después de que se carga el árbol
    const drawLeavesAfterTree = () => {
      // Configuraciones para posicionamiento de hojas
      const treeCenterX = width / 2;
      const treeCenterY = isMobile ? height - 200 : height - 280;

      // Dibujar hojas usando espiral de Fibonacci para distribución más natural
      votos.forEach((voto, index) => {
        const angle = index * 2.4; // Ángulo dorado
        const radius = isMobile ? (50 + Math.sqrt(index) * 8) : (100 + Math.sqrt(index) * 18);

        // Agregar variación aleatoria para hacer más natural
        const randomAngle = (Math.random() - 0.5) * 0.5;
        const randomRadius = (Math.random() - 0.5) * 20;

        const leafX = treeCenterX + Math.cos(angle + randomAngle) * (radius + randomRadius);
        const leafY = treeCenterY + Math.sin(angle + randomAngle) * (radius + randomRadius) * 0.6; // Más ovalado

        const leafSize = 87 + Math.random() * 38; // Tamaño aleatorio para las hojas
        const leafColor = voto.prediccion === 'varon' ? '#1E40AF' : '#BE185D';
        const leafAngle = Math.random() * Math.PI * 2; // Rotación aleatoria

        drawLeaf(ctx, leafX, leafY, leafSize, leafAngle, leafColor);
      });
    };

    // Intentar cargar la imagen del árbol, si no existe usar el árbol dibujado
    treeImage.onerror = () => {
      // Si no se puede cargar la imagen, dibujar el árbol original
      drawOriginalTree();
      drawLeavesAfterTree();
    };

    treeImage.src = '/arbol.png';

    // Función para dibujar el árbol original si no hay imagen
    const drawOriginalTree = () => {
      // Dibujar tronco del árbol con gradiente (estirado hasta abajo, ajustado para móvil)
      const trunkGradient = ctx.createLinearGradient(width / 2 - 25, height - 280, width / 2 + 25, height - 280);
      trunkGradient.addColorStop(0, '#8B4513');
      trunkGradient.addColorStop(0.5, '#A0522D');
      trunkGradient.addColorStop(1, '#8B4513');
      ctx.fillStyle = trunkGradient;
      const trunkHeight = isMobile ? 250 : 300;
      const trunkY = isMobile ? (height - 250) : (height - 300);
      ctx.fillRect(width / 2 - 25, trunkY, 50, trunkHeight);

      // Dibujar ramas principales con mejor distribución
      ctx.strokeStyle = '#654321';
      ctx.lineWidth = 10;
      ctx.lineCap = 'round';

      // Posiciones base para las ramas (ajustadas para móvil)
      const branchBaseY = isMobile ? (height - 250) : (height - 310);
      const branchMidY = isMobile ? (height - 280) : (height - 340);
      const branchEndY = isMobile ? (height - 300) : (height - 360);
      const branchSecY = isMobile ? (height - 270) : (height - 330);
      const branchSecMidY = isMobile ? (height - 290) : (height - 350);
      const branchSecEndY = isMobile ? (height - 310) : (height - 370);
      const branchTopY = isMobile ? (height - 260) : (height - 320);
      const branchTopMidY = isMobile ? (height - 300) : (height - 360);
      const branchTopEndY = isMobile ? (height - 340) : (height - 400);

      // Rama izquierda principal
      ctx.beginPath();
      ctx.moveTo(width / 2 - 15, branchBaseY);
      ctx.quadraticCurveTo(width / 2 - 60, branchMidY, width / 2 - 100, branchEndY);
      ctx.stroke();

      // Rama derecha principal
      ctx.beginPath();
      ctx.moveTo(width / 2 + 15, branchBaseY);
      ctx.quadraticCurveTo(width / 2 + 60, branchMidY, width / 2 + 100, branchEndY);
      ctx.stroke();

      // Ramas secundarias izquierdas
      ctx.lineWidth = 6;
      ctx.beginPath();
      ctx.moveTo(width / 2 - 8, branchSecY);
      ctx.quadraticCurveTo(width / 2 - 40, branchSecMidY, width / 2 - 70, branchSecEndY);
      ctx.stroke();

      // Ramas secundarias derechas
      ctx.beginPath();
      ctx.moveTo(width / 2 + 8, branchSecY);
      ctx.quadraticCurveTo(width / 2 + 40, branchSecMidY, width / 2 + 70, branchSecEndY);
      ctx.stroke();

      // Ramas superiores
      ctx.lineWidth = 4;
      ctx.beginPath();
      ctx.moveTo(width / 2, branchTopY);
      ctx.quadraticCurveTo(width / 2 - 30, branchTopMidY, width / 2 - 50, branchTopEndY);
      ctx.stroke();

      ctx.beginPath();
      ctx.moveTo(width / 2, branchTopY);
      ctx.quadraticCurveTo(width / 2 + 30, branchTopMidY, width / 2 + 50, branchTopEndY);
      ctx.stroke();
    };

  }, [votos]);

  return (
    <div style={{ width: '100%', height: '500px', border: '2px solid #ddd', borderRadius: '10px', overflow: 'hidden' }}>
      <canvas
        ref={canvasRef}
        style={{ width: '100%', height: '100%', display: 'block' }}
      />

      {/* Leyenda */}
      <div style={{
        position: 'absolute',
        bottom: '20px',
        left: '20px',
        background: 'rgba(255, 255, 255, 0.9)',
        padding: '15px',
        borderRadius: '10px',
        backdropFilter: 'blur(10px)',
        border: '1px solid rgba(255, 255, 255, 0.3)'
      }}>
        {!window.innerWidth || window.innerWidth > 768 && (
          <h4 style={{ margin: '0 0 10px 0', color: '#333', fontSize: '1rem' }}>Leyenda</h4>
        )}
        <div style={{ display: 'flex', flexDirection: window.innerWidth <= 768 ? 'row' : 'column', gap: '8px', alignItems: 'center' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
            <div style={{ width: '20px', height: '15px', backgroundColor: '#1E40AF', borderRadius: '50%' }}></div>
            <span style={{ fontSize: '0.9rem', color: '#333' }}>Niño</span>
          </div>
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
            <div style={{ width: '20px', height: '15px', backgroundColor: '#BE185D', borderRadius: '50%' }}></div>
            <span style={{ fontSize: '0.9rem', color: '#333' }}>Niña</span>
          </div>
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginTop: window.innerWidth <= 768 ? '0' : '5px' }}>
            <span style={{ fontSize: '1.2rem' }}>🌿</span>
            <span style={{ fontSize: '0.9rem', color: '#333' }}>{votos.length}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TreeVisualization;
