import React from 'react';
import { motion } from 'framer-motion';

interface Voto {
  id: number;
  nombre: string;
  prediccion: 'varon' | 'nina';
  pais?: string;
  comentario?: string;
  foto_url?: string;
  fecha_voto: string;
}

// Mapeo de c贸digos de pa铆s a banderas
const getBandera = (codigoPais: string) => {
  const banderas: { [key: string]: string } = {
    'AR': '', 'BO': 'ю', 'BR': 'ю', 'CL': '', 'CO': '', 'CR': '', 'CU': '', 'DO': '', 'EC': '', 'SV': '葛',
    'ES': '', 'GT': '', 'HN': '', 'MX': '拆', 'NI': '仇', 'PA': '叼', 'PY': '叼', 'PE': '叼', 'PR': '叼', 'UY': '吼',
    'VE': '火', 'US': '吼', 'CA': '', 'FR': '', 'DE': '', 'IT': '', 'GB': '', 'AU': '', 'NZ': '仇', 'JP': '',
    'CN': '', 'IN': '', 'RU': '佛', 'OT': ''
  };
  return banderas[codigoPais] || '';
};

interface VotosListProps {
  votos: Voto[];
}

const VotosList: React.FC<VotosListProps> = ({ votos }) => {
  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('es-ES', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (votos.length === 0) {
    return (
      <div style={{ textAlign: 'center', padding: '40px' }}>
        <div style={{ fontSize: '4rem', marginBottom: '20px' }}></div>
        <h3>隆S茅 el primero en votar!</h3>
        <p style={{ color: '#666' }}>
          A煤n no hay predicciones. 隆Agrega la tuya y ve c贸mo crece el 谩rbol!
        </p>
      </div>
    );
  }

  return (
    <div className="votos-list">
      {votos.map((voto, index) => (
        <motion.div
          key={voto.id}
          className={`voto-card ${voto.prediccion}`}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: index * 0.1, duration: 0.5 }}
          whileHover={{ scale: 1.02 }}
        >
          <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
            {voto.foto_url && (
              <img
                src={`http://192.168.18.49:5000${voto.foto_url}`}
                alt={`Foto de ${voto.nombre}`}
                className="voto-foto"
                onError={(e) => {
                  const target = e.target as HTMLImageElement;
                  target.style.display = 'none';
                }}
              />
            )}

            {voto.pais && (
              <div style={{ fontSize: '2rem' }} title={`Pa铆s: ${voto.pais}`}>
                {getBandera(voto.pais)}
              </div>
            )}
          </div>

          <div className="voto-nombre">{voto.nombre}</div>

          <div className={`voto-prediccion ${voto.prediccion}`}>
            {voto.prediccion === 'varon' ? ' Var贸n' : ' Ni帽a'}
          </div>

          {voto.comentario && (
            <div className="voto-comentario">
              "{voto.comentario}"
            </div>
          )}

          <div className="voto-fecha">
            {formatDate(voto.fecha_voto)}
          </div>
        </motion.div>
      ))}
    </div>
  );
};

export default VotosList;
