import React from 'react';
import { motion } from 'framer-motion';

interface Estadisticas {
  total_votos: number;
  votos_varon: number;
  votos_nina: number;
}

interface StatsProps {
  estadisticas: Estadisticas;
}

const Stats: React.FC<StatsProps> = ({ estadisticas }) => {
  const porcentajeVaron = estadisticas.total_votos > 0 
    ? Math.round((estadisticas.votos_varon / estadisticas.total_votos) * 100) 
    : 0;

  const porcentajeNina = estadisticas.total_votos > 0 
    ? Math.round((estadisticas.votos_nina / estadisticas.total_votos) * 100) 
    : 0;

  return (
    <div className="stats">
      <motion.div 
        className="stat-card"
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ delay: 0.1, duration: 0.5 }}
      >
        <div className="stat-number">ðŸ‘¶</div>
        <div className="stat-label">Total de Votos</div>
        <div className="stat-number">{estadisticas.total_votos}</div>
      </motion.div>

      <motion.div 
        className="stat-card"
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ delay: 0.2, duration: 0.5 }}
        style={{ background: 'linear-gradient(135deg, #3b82f6 0%, #1d4ed8 100%)' }}
      >
        <div className="stat-number">ðŸ‘¦</div>
        <div className="stat-label">Predicen VarÃ³n</div>
        <div className="stat-number">{estadisticas.votos_varon}</div>
        <div className="stat-label">({porcentajeVaron}%)</div>
      </motion.div>

      <motion.div 
        className="stat-card"
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ delay: 0.3, duration: 0.5 }}
        style={{ background: 'linear-gradient(135deg, #ec4899 0%, #be185d 100%)' }}
      >
        <div className="stat-number">ðŸ‘§</div>
        <div className="stat-label">Predicen NiÃ±a</div>
        <div className="stat-number">{estadisticas.votos_nina}</div>
        <div className="stat-label">({porcentajeNina}%)</div>
      </motion.div>
    </div>
  );
};

export default Stats;
