import React, { useRef } from 'react';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import { motion } from 'framer-motion';

interface PDFGeneratorProps {
  votos: any[];
  estadisticas: any;
  treeRef: React.RefObject<HTMLDivElement>;
}

const PDFGenerator: React.FC<PDFGeneratorProps> = ({ votos, estadisticas, treeRef }) => {
  const [generating, setGenering] = React.useState(false);

  const generatePDF = async () => {
    if (!treeRef.current) return;

    setGenering(true);

    try {
      const pdf = new jsPDF('p', 'mm', 'a4');
      const pageWidth = pdf.internal.pageSize.getWidth();
      const pageHeight = pdf.internal.pageSize.getHeight();

      // Configurar fuentes y colores
      pdf.setFont('helvetica');

      // T√≠tulo principal
      pdf.setFontSize(24);
      pdf.setTextColor(51, 51, 51);
      pdf.text('Arbol de Predicciones', pageWidth / 2, 25, { align: 'center' });

      // Subt√≠tulo
      pdf.setFontSize(14);
      pdf.setTextColor(102, 102, 102);
      pdf.text('Un recuerdo especial para los futuros padres', pageWidth / 2, 35, { align: 'center' });

      // Fecha
      pdf.setFontSize(12);
      pdf.setTextColor(153, 153, 153);
      const fecha = new Date().toLocaleDateString('es-ES', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });
      pdf.text(`Generado el ${fecha}`, pageWidth / 2, 45, { align: 'center' });

      // Capturar imagen del √°rbol
      const treeCanvas = await html2canvas(treeRef.current, {
        scale: 2,
        useCORS: true,
        allowTaint: true,
        backgroundColor: '#ffffff',
        logging: false
      });

      const treeImgData = treeCanvas.toDataURL('image/png');
      const treeAspectRatio = treeCanvas.width / treeCanvas.height;
      const treeWidth = pageWidth - 20;
      const treeHeight = treeWidth / treeAspectRatio;

      // Agregar imagen del √°rbol
      pdf.addImage(treeImgData, 'PNG', 10, 55, treeWidth, treeHeight);

      // Si el √°rbol es muy alto, crear nueva p√°gina para estad√≠sticas
      const currentY = 55 + treeHeight + 10;

      if (currentY > pageHeight - 80) {
        pdf.addPage();
        pdf.setFontSize(20);
        pdf.setTextColor(51, 51, 51);
        pdf.text('Estadisticas de las Predicciones', pageWidth / 2, 25, { align: 'center' });
      } else {
        pdf.setFontSize(20);
        pdf.setTextColor(51, 51, 51);
        pdf.text('Estadisticas de las Predicciones', pageWidth / 2, currentY, { align: 'center' });
      }

      // Estad√≠sticas
      const statsY = currentY > pageHeight - 80 ? 35 : currentY + 10;
      pdf.setFontSize(14);
      pdf.setTextColor(30, 64, 175);
      pdf.text(`Predicen Nino: ${estadisticas.votos_varon} (${Math.round((estadisticas.votos_varon / estadisticas.total_votos) * 100)}%)`, 20, statsY);

      pdf.setTextColor(190, 24, 93);
      pdf.text(`Predicen Nina: ${estadisticas.votos_nina} (${Math.round((estadisticas.votos_nina / estadisticas.total_votos) * 100)}%)`, 20, statsY + 8);

      pdf.setTextColor(51, 51, 51);
      pdf.text(`Total de Votos: ${estadisticas.total_votos}`, 20, statsY + 16);

      // Comentarios especiales
      const commentsY = statsY + 30;
      pdf.setFontSize(16);
      pdf.setTextColor(51, 51, 51);
      pdf.text('Mensajes Especiales', pageWidth / 2, commentsY, { align: 'center' });

      // Filtrar comentarios que tengan contenido
      const comentariosConContenido = votos.filter(voto => 
        voto.comentario && 
        voto.comentario.trim().length > 0 && 
        voto.comentario.trim() !== 'undefined'
      );

      let currentCommentY = commentsY + 15;
      let commentCount = 0;

      comentariosConContenido.forEach((voto, index) => {
        if (currentCommentY > pageHeight - 30) {
          pdf.addPage();
          pdf.setFontSize(16);
          pdf.setTextColor(51, 51, 51);
          pdf.text('Mensajes Especiales (continuacion)', pageWidth / 2, 25, { align: 'center' });
          currentCommentY = 35;
        }

        // Nombre y pa√≠s
        pdf.setFontSize(12);
        pdf.setTextColor(30, 64, 175);
        const banderas: { [key: string]: string } = {
          'AR': 'Argentina', 'BO': 'Bolivia', 'BR': 'Brasil', 'CL': 'Chile', 'CO': 'Colombia', 'CR': 'Costa Rica', 'CU': 'Cuba', 'DO': 'Republica Dominicana', 'EC': 'Ecuador', 'SV': 'El Salvador',
          'ES': 'Espana', 'GT': 'Guatemala', 'HN': 'Honduras', 'MX': 'Mexico', 'NI': 'Nicaragua', 'PA': 'Panama', 'PY': 'Paraguay', 'PE': 'Peru', 'PR': 'Puerto Rico', 'UY': 'Uruguay',
          'VE': 'Venezuela', 'US': 'Estados Unidos', 'CA': 'Canada', 'FR': 'Francia', 'DE': 'Alemania', 'IT': 'Italia', 'GB': 'Reino Unido', 'AU': 'Australia', 'NZ': 'Nueva Zelanda', 'JP': 'Japon',
          'CN': 'China', 'IN': 'India', 'RU': 'Rusia', 'OT': 'Otro'
        };
        const pais = banderas[voto.pais] || 'Otro';
        pdf.text(`${voto.nombre} (${pais})`, 20, currentCommentY);

        // Comentario
        pdf.setFontSize(10);
        pdf.setTextColor(51, 51, 51);
        const comentario = `"${voto.comentario}"`;
        const lines = pdf.splitTextToSize(comentario, pageWidth - 40);
        pdf.text(lines, 20, currentCommentY + 5);

        currentCommentY += 8 + (lines.length * 4);
        commentCount++;

        if (commentCount < comentariosConContenido.length) {
          // L√≠nea separadora
          pdf.setDrawColor(230, 230, 230);
          pdf.line(20, currentCommentY - 2, pageWidth - 20, currentCommentY - 2);
          currentCommentY += 5;
        }
      });

      // P√°gina final con mensaje especial
      pdf.addPage();
      pdf.setFontSize(18);
      pdf.setTextColor(51, 51, 51);
      pdf.text('¬°Felicidades Futuros Padres!', pageWidth / 2, 40, { align: 'center' });

      pdf.setFontSize(12);
      pdf.setTextColor(102, 102, 102);
      const mensajeFinal = [
        'Este arbol representa el amor y las buenas vibras que',
        'toda su familia y amigos tienen para ustedes.',
        '',
        'Cada hoja es una prediccion llena de carino y',
        'esperanza para el nuevo miembro de la familia.',
        '',
        '¬°Que este bebe traiga mucha alegria y bendiciones',
        'a sus vidas!'
      ];

      mensajeFinal.forEach((line, index) => {
        pdf.text(line, pageWidth / 2, 60 + (index * 8), { align: 'center' });
      });

      // Guardar el PDF
      pdf.save('arbol-predicciones-recuerdo.pdf');

    } catch (error) {
      console.error('Error generando PDF:', error);
      alert('Error al generar el PDF. Int√©ntalo de nuevo.');
    } finally {
      setGenering(false);
    }
  };

  return (
    <motion.button
      onClick={generatePDF}
      disabled={generating}
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
      style={{
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        color: 'white',
        border: 'none',
        borderRadius: '15px',
        padding: '18px 30px',
        fontSize: '1.1rem',
        fontWeight: '600',
        cursor: generating ? 'not-allowed' : 'pointer',
        width: '100%',
        marginTop: '20px',
        boxShadow: '0 4px 15px rgba(102, 126, 234, 0.4)',
        transition: 'all 0.3s ease',
        opacity: generating ? 0.7 : 1
      }}
    >
      {generating ? (
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px' }}>
          <div style={{ 
            width: '20px', 
            height: '20px', 
            border: '2px solid rgba(255,255,255,0.3)', 
            borderTop: '2px solid white', 
            borderRadius: '50%', 
            animation: 'spin 1s linear infinite' 
          }} />
          Generando recuerdo...
        </div>
      ) : (
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px' }}>
          <span style={{ fontSize: '1.3rem' }}>üìÑ</span>
          <span>Un Recuerdo para los Padres</span>
        </div>
      )}
    </motion.button>
  );
};

export default PDFGenerator;
