import React, { useRef } from 'react';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import { motion } from 'framer-motion';

interface PDFGeneratorProps {
  votos: any[];
  estadisticas: any;
  treeRef: React.RefObject<HTMLDivElement>;
}

const PDFGenerator: React.FC<PDFGeneratorProps> = ({ votos, estadisticas, treeRef }) => {
  const [generating, setGenering] = React.useState(false);

  const generatePDF = async () => {
    if (!treeRef.current) return;

    setGenering(true);

    try {
      const pdf = new jsPDF('p', 'mm', 'a4');
      const pageWidth = pdf.internal.pageSize.getWidth();
      const pageHeight = pdf.internal.pageSize.getHeight();

      // Configurar fuentes modernas
      pdf.setFont('helvetica');

      // P치gina 1: Portada con encabezado
      pdf.setFillColor(248, 250, 252); // Fondo lavanda muy claro
      pdf.rect(0, 0, pageWidth, pageHeight, 'F');

      // Encabezado principal
      pdf.setFontSize(24);
      pdf.setTextColor(51, 51, 51);
      pdf.setFont('helvetica', 'bold');
      pdf.text('Arbol de Predicciones - Un Recuerdo Especial', pageWidth / 2, 30, { align: 'center' });

      // Capturar imagen del 치rbol
      const treeCanvas = await html2canvas(treeRef.current, {
        scale: 1.5,
        useCORS: true,
        allowTaint: true,
        backgroundColor: '#ffffff',
        logging: false,
        width: treeRef.current.offsetWidth,
        height: treeRef.current.offsetHeight
      });

      const treeImgData = treeCanvas.toDataURL('image/png');
      const treeAspectRatio = treeCanvas.width / treeCanvas.height;
      const treeWidth = pageWidth - 40;
      const treeHeight = treeWidth / treeAspectRatio;

      // Agregar imagen del 치rbol centrada
      pdf.addImage(treeImgData, 'PNG', 20, 50, treeWidth, treeHeight);

      // Fecha de generaci칩n
      pdf.setFontSize(12);
      pdf.setTextColor(153, 153, 153);
      pdf.setFont('helvetica', 'normal');
      const fecha = new Date().toLocaleDateString('es-ES', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });
      pdf.text(`Generado el ${fecha}`, pageWidth / 2, pageHeight - 20, { align: 'center' });

      // P치gina 2: Estad칤sticas en tarjetas modernas
      pdf.addPage();
      pdf.setFillColor(248, 250, 252);
      pdf.rect(0, 0, pageWidth, pageHeight, 'F');

      // T칤tulo de estad칤sticas
      pdf.setFontSize(20);
      pdf.setTextColor(51, 51, 51);
      pdf.setFont('helvetica', 'bold');
      pdf.text('Estadisticas de las Predicciones', pageWidth / 2, 30, { align: 'center' });

      // Dibujar tarjetas de estad칤sticas modernas
      const cardWidth = 55;
      const cardHeight = 35;
      const startX = (pageWidth - (cardWidth * 3 + 20)) / 2;

      // Tarjeta Total
      pdf.setFillColor(255, 255, 255);
      pdf.setDrawColor(230, 230, 230);
      pdf.setLineWidth(0.5);
      pdf.roundedRect(startX, 50, cardWidth, cardHeight, 8, 8, 'FD');
      pdf.setTextColor(102, 126, 234);
      pdf.setFontSize(18);
      pdf.setFont('helvetica', 'bold');
      pdf.text(estadisticas.total_votos.toString(), startX + cardWidth/2, 65, { align: 'center' });
      pdf.setFontSize(10);
      pdf.setTextColor(102, 102, 102);
      pdf.setFont('helvetica', 'normal');
      pdf.text('Total Votos', startX + cardWidth/2, 75, { align: 'center' });

      // Tarjeta Ni침o
      pdf.setFillColor(255, 255, 255);
      pdf.roundedRect(startX + cardWidth + 10, 50, cardWidth, cardHeight, 8, 8, 'FD');
      pdf.setTextColor(30, 64, 175);
      pdf.setFontSize(18);
      pdf.setFont('helvetica', 'bold');
      pdf.text(estadisticas.votos_varon.toString(), startX + cardWidth + 10 + cardWidth/2, 65, { align: 'center' });
      pdf.setFontSize(10);
      pdf.setTextColor(102, 102, 102);
      pdf.setFont('helvetica', 'normal');
      pdf.text('Nino', startX + cardWidth + 10 + cardWidth/2, 75, { align: 'center' });

      // Tarjeta Ni침a
      pdf.setFillColor(255, 255, 255);
      pdf.roundedRect(startX + (cardWidth + 10) * 2, 50, cardWidth, cardHeight, 8, 8, 'FD');
      pdf.setTextColor(190, 24, 93);
      pdf.setFontSize(18);
      pdf.setFont('helvetica', 'bold');
      pdf.text(estadisticas.votos_nina.toString(), startX + (cardWidth + 10) * 2 + cardWidth/2, 65, { align: 'center' });
      pdf.setFontSize(10);
      pdf.setTextColor(102, 102, 102);
      pdf.setFont('helvetica', 'normal');
      pdf.text('Nina', startX + (cardWidth + 10) * 2 + cardWidth/2, 75, { align: 'center' });

      // Porcentajes en c칤rculos peque침os
      pdf.setFontSize(12);
      const porcentajeNino = Math.round((estadisticas.votos_varon / estadisticas.total_votos) * 100);
      const porcentajeNina = Math.round((estadisticas.votos_nina / estadisticas.total_votos) * 100);

      // C칤rculo para ni침o
      pdf.setFillColor(212, 240, 247); // Celeste suave
      pdf.circle(startX + cardWidth + 10 + cardWidth/2, 90, 8, 'F');
      pdf.setTextColor(30, 64, 175);
      pdf.setFontSize(10);
      pdf.setFont('helvetica', 'bold');
      pdf.text(`${porcentajeNino}%`, startX + cardWidth + 10 + cardWidth/2, 93, { align: 'center' });

      // C칤rculo para ni침a
      pdf.setFillColor(250, 218, 221); // Rosa suave
      pdf.circle(startX + (cardWidth + 10) * 2 + cardWidth/2, 90, 8, 'F');
      pdf.setTextColor(190, 24, 93);
      pdf.text(`${porcentajeNina}%`, startX + (cardWidth + 10) * 2 + cardWidth/2, 93, { align: 'center' });

      // Filtrar comentarios v치lidos
      const comentariosValidos = votos.filter(voto => 
        voto.comentario && 
        voto.comentario.trim().length > 0 && 
        voto.comentario.trim() !== 'undefined' &&
        voto.comentario.trim() !== 'null'
      );

      if (comentariosValidos.length > 0) {
        // P치gina 3+: Comentarios en rejilla de 3 columnas
        pdf.addPage();
        pdf.setFillColor(248, 250, 252);
        pdf.rect(0, 0, pageWidth, pageHeight, 'F');

        // T칤tulo de la p치gina
        pdf.setFontSize(20);
        pdf.setTextColor(51, 51, 51);
        pdf.setFont('helvetica', 'bold');
        pdf.text('Mensajes Especiales', pageWidth / 2, 25, { align: 'center' });

        // Configuraci칩n de la rejilla
        const marginX = 15;
        const marginY = 40;
        const cardWidth = (pageWidth - (marginX * 2) - 20) / 3; // 3 columnas con espacio entre ellas
        const cardHeight = 70; // Aumentado para m치s espacio
        const cardSpacing = 10;

        let currentRow = 0;
        let currentCol = 0;
        let pageCount = 3;

        // Cache de banderas para evitar m칰ltiples descargas
        const flagCache = new Map<string, HTMLImageElement>();

        for (let index = 0; index < comentariosValidos.length; index++) {
          const voto = comentariosValidos[index];

          // Verificar si necesitamos nueva p치gina
          const currentY = marginY + (currentRow * (cardHeight + cardSpacing));
          if (currentY + cardHeight > pageHeight - 30) {
            pdf.addPage();
            pdf.setFillColor(248, 250, 252);
            pdf.rect(0, 0, pageWidth, pageHeight, 'F');
            pdf.setFontSize(20);
            pdf.setTextColor(51, 51, 51);
            pdf.setFont('helvetica', 'bold');
            pdf.text(`Mensajes Especiales (Pagina ${pageCount})`, pageWidth / 2, 25, { align: 'center' });
            currentRow = 0;
            currentCol = 0;
            pageCount++;
          }

          const cardX = marginX + (currentCol * (cardWidth + 10));
          const cardY = marginY + (currentRow * (cardHeight + cardSpacing));

          // Dibujar card con estilo moderno
          pdf.setFillColor(255, 255, 255);
          pdf.setDrawColor(230, 230, 230);
          pdf.setLineWidth(0.5);
          pdf.roundedRect(cardX, cardY, cardWidth, cardHeight, 8, 8, 'FD');

          // Agregar sombra sutil (simulada con borde m치s oscuro)
          pdf.setDrawColor(200, 200, 200);
          pdf.setLineWidth(0.3);
          pdf.roundedRect(cardX + 1, cardY + 1, cardWidth, cardHeight, 8, 8, 'S');

          // Banderas reales
          const banderas: { [key: string]: string } = {
            'AR': '游뷣릖', 'BO': '游游', 'BR': '游游', 'CL': '游뻟릖', 'CO': '游뻟릖', 'CR': '游뻟릖', 'CU': '游뻟릖', 'DO': '游뾇릖', 'EC': '游쀯릖', 'SV': '游젏릖',
            'ES': '游쀯릖', 'GT': '游섫릖', 'HN': '游쇓릖', 'MX': '游쓇릖', 'NI': '游游', 'PA': '游왫릖', 'PY': '游왫릖', 'PE': '游왫릖', 'PR': '游왫릖', 'UY': '游쥟릖',
            'VE': '游游', 'US': '游쥟릖', 'CA': '游뻟릖', 'FR': '游游', 'DE': '游뾇릖', 'IT': '游쉻릖', 'GB': '游섫릖', 'AU': '游뷣릖', 'NZ': '游游', 'JP': '游游',
            'CN': '游뻟릖', 'IN': '游쉻릖', 'RU': '游游', 'OT': '游깴'
          };
          const bandera = banderas[voto.pais] || '游깴';

          // Limpiar y validar el nombre
          let nombreLimpio = voto.nombre?.toString().trim() || 'Anonimo';
          nombreLimpio = nombreLimpio.toString().trim();
          if (nombreLimpio === 'undefined' || nombreLimpio === 'null') {
            nombreLimpio = 'Anonimo';
          }

          // Nombre y bandera (t칤tulo)
          pdf.setFontSize(11);
          pdf.setTextColor(51, 51, 51);
          pdf.setFont('helvetica', 'bold');
          const nombreCompleto = `${bandera} ${nombreLimpio}`;
          pdf.text(nombreCompleto, cardX + 8, cardY + 12);

          // Etiqueta de g칠nero (subt칤tulo)
          pdf.setFontSize(9);
          pdf.setFont('helvetica', 'normal');

          if (voto.prediccion === 'varon') {
            // Etiqueta celeste para ni침o
            pdf.setFillColor(212, 240, 247); // Celeste suave
            pdf.roundedRect(cardX + 8, cardY + 20, 25, 8, 4, 4, 'F');
            pdf.setTextColor(30, 64, 175);
            pdf.text('Nino', cardX + 12, cardY + 25);
          } else {
            // Etiqueta rosa para ni침a
            pdf.setFillColor(250, 218, 221); // Rosa suave
            pdf.roundedRect(cardX + 8, cardY + 20, 25, 8, 4, 4, 'F');
            pdf.setTextColor(190, 24, 93);
            pdf.text('Nina', cardX + 12, cardY + 25);
          }

          // Limpiar y validar el comentario
          let comentarioLimpio = voto.comentario || 'Sin comentario';
          comentarioLimpio = comentarioLimpio.toString().trim();
          if (comentarioLimpio === 'undefined' || comentarioLimpio === 'null') {
            comentarioLimpio = 'Sin comentario';
          }

          // Comentario centrado
          pdf.setFontSize(8);
          pdf.setTextColor(51, 51, 51);
          pdf.setFont('helvetica', 'normal');

          const comentario = `"${comentarioLimpio}"`;
          const lines = pdf.splitTextToSize(comentario, cardWidth - 16);

          // Centrar el texto verticalmente en el espacio restante
          const textHeight = lines.length * 3.5;
          const startTextY = cardY + 35 + (cardHeight - 35 - textHeight - 15) / 2; // Dejar espacio para fecha

          lines.forEach((line: string, lineIndex: number) => {
            pdf.text(line, cardX + 8, startTextY + (lineIndex * 3.5), { align: 'center' });
          });

          // Fecha en la parte inferior - MEJORADA
          pdf.setFontSize(7);
          pdf.setTextColor(153, 153, 153);

          // Manejar fechas de forma segura
          let fechaTexto = 'Fecha no disponible';
          try {
            if (voto.fecha) {
              const fecha = new Date(voto.fecha);
              if (!isNaN(fecha.getTime())) {
                const fechaVoto = fecha.toLocaleDateString('es-ES', {
                  day: 'numeric',
                  month: 'short',
                  year: 'numeric'
                });
                const horaVoto = fecha.toLocaleTimeString('es-ES', {
                  hour: '2-digit',
                  minute: '2-digit'
                });
                fechaTexto = `${fechaVoto}, ${horaVoto}`;
              }
            }
          } catch (error) {
            console.log('Error procesando fecha:', voto.fecha);
          }

          pdf.text(fechaTexto, cardX + 8, cardY + cardHeight - 8);

          // Intentar cargar foto si existe - FOTO CIRCULAR
          if (voto.foto_url) {
            try {
              const img = new Image();
              img.crossOrigin = 'anonymous';
              img.src = `http://192.168.18.49:5000${voto.foto_url}`;

              await new Promise((resolve, reject) => {
                img.onload = resolve;
                img.onerror = reject;
                setTimeout(reject, 3000);
              });

              // Crear foto circular en la esquina superior derecha
              const fotoSize = 20;
              const fotoX = cardX + cardWidth - fotoSize - 5;
              const fotoY = cardY + 5;

              // Fondo circular blanco
              pdf.setFillColor(255, 255, 255);
              pdf.circle(fotoX + fotoSize/2, fotoY + fotoSize/2, fotoSize/2, 'F');

              // Borde circular
              pdf.setDrawColor(230, 230, 230);
              pdf.setLineWidth(0.5);
              pdf.circle(fotoX + fotoSize/2, fotoY + fotoSize/2, fotoSize/2, 'S');

              // Agregar la imagen (se recortar치 autom치ticamente al c칤rculo)
              pdf.addImage(img, 'JPEG', fotoX, fotoY, fotoSize, fotoSize);

            } catch (error) {
              console.log('No se pudo cargar la foto:', voto.foto_url);

              // Placeholder circular si no se puede cargar la foto
              pdf.setFillColor(229, 231, 235);
              pdf.circle(cardX + cardWidth - 15, cardY + 15, 10, 'F');
              pdf.setDrawColor(209, 213, 219);
              pdf.setLineWidth(0.5);
              pdf.circle(cardX + cardWidth - 15, cardY + 15, 10, 'S');
            }
          }

          // Avanzar a la siguiente posici칩n en la rejilla
          currentCol++;
          if (currentCol >= 3) {
            currentCol = 0;
            currentRow++;
          }
        }
      }

      // P치gina final: Mensaje especial destacado
      pdf.addPage();
      pdf.setFillColor(248, 250, 252);
      pdf.rect(0, 0, pageWidth, pageHeight, 'F');

      // T칤tulo principal
      pdf.setFontSize(24);
      pdf.setTextColor(51, 51, 51);
      pdf.setFont('helvetica', 'bold');
      pdf.text('춰Felicidades Futuros Padres!', pageWidth / 2, 50, { align: 'center' });

      // Card destacada para el mensaje final
      const finalCardWidth = pageWidth - 40;
      const finalCardHeight = 120;
      const finalCardX = 20;
      const finalCardY = 70;

      // Fondo de la card con gradiente (simulado)
      pdf.setFillColor(255, 255, 255);
      pdf.setDrawColor(230, 230, 230);
      pdf.setLineWidth(1);
      pdf.roundedRect(finalCardX, finalCardY, finalCardWidth, finalCardHeight, 12, 12, 'FD');

      // Borde decorativo
      pdf.setDrawColor(102, 126, 234);
      pdf.setLineWidth(2);
      pdf.roundedRect(finalCardX, finalCardY, finalCardWidth, finalCardHeight, 12, 12, 'S');

      // Mensaje final
      pdf.setFontSize(14);
      pdf.setTextColor(51, 51, 51);
      pdf.setFont('helvetica', 'normal');

      const mensajeFinal = [
        'Este arbol representa el amor y las buenas vibras que',
        'toda su familia y amigos tienen para ustedes.',
        '',
        'Cada hoja es una prediccion llena de carino y',
        'esperanza para el nuevo miembro de la familia.',
        '',
        '춰Que este bebe traiga mucha alegria y bendiciones',
        'a sus vidas!'
      ];

      mensajeFinal.forEach((line, index) => {
        if (line.trim() !== '') {
          pdf.text(line, pageWidth / 2, finalCardY + 25 + (index * 12), { align: 'center' });
        }
      });

      // Firma o sello final
      pdf.setFontSize(12);
      pdf.setTextColor(102, 126, 234);
      pdf.setFont('helvetica', 'italic');
      pdf.text('Con mucho amor y buenos deseos', pageWidth / 2, finalCardY + finalCardHeight + 20, { align: 'center' });

      // Guardar el PDF
      pdf.save('arbol-predicciones-recuerdo-especial.pdf');

    } catch (error) {
      console.error('Error generando PDF:', error);
      alert('Error al generar el PDF. Int칠ntalo de nuevo.');
    } finally {
      setGenering(false);
    }
  };

  return (
    <motion.button
      onClick={generatePDF}
      disabled={generating}
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
      style={{
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        color: 'white',
        border: 'none',
        borderRadius: '15px',
        padding: '18px 30px',
        fontSize: '1.1rem',
        fontWeight: '600',
        cursor: generating ? 'not-allowed' : 'pointer',
        width: '100%',
        marginTop: '20px',
        boxShadow: '0 4px 15px rgba(102, 126, 234, 0.4)',
        transition: 'all 0.3s ease',
        opacity: generating ? 0.7 : 1
      }}
    >
      {generating ? (
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px' }}>
          <div style={{ 
            width: '20px', 
            height: '20px', 
            border: '2px solid rgba(255,255,255,0.3)', 
            borderTop: '2px solid white', 
            borderRadius: '50%', 
            animation: 'spin 1s linear infinite' 
          }} />
          Generando recuerdo especial...
        </div>
      ) : (
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px' }}>
          <span style={{ fontSize: '1.3rem' }}>游늯</span>
          <span>Crear Recuerdo Especial</span>
        </div>
      )}
    </motion.button>
  );
