import React from 'react';
import { motion } from 'framer-motion';

interface Voto {
  id: number;
  nombre: string;
  prediccion: 'varon' | 'nina';
  pais?: string;
  comentario?: string;
  foto_url?: string;
  fecha_voto: string;
}

// Mapeo de c칩digos de pa칤s a banderas
const getBandera = (codigoPais: string) => {
  const banderas: { [key: string]: string } = {
    'AR': '游뷣릖', 'BO': '游游', 'BR': '游游', 'CL': '游뻟릖', 'CO': '游뻟릖', 'CR': '游뻟릖', 'CU': '游뻟릖', 'DO': '游뾇릖', 'EC': '游쀯릖', 'SV': '游젏릖',
    'ES': '游쀯릖', 'GT': '游섫릖', 'HN': '游쇓릖', 'MX': '游쓇릖', 'NI': '游游', 'PA': '游왫릖', 'PY': '游왫릖', 'PE': '游왫릖', 'PR': '游왫릖', 'UY': '游쥟릖',
    'VE': '游游', 'US': '游쥟릖', 'CA': '游뻟릖', 'FR': '游游', 'DE': '游뾇릖', 'IT': '游쉻릖', 'GB': '游섫릖', 'AU': '游뷣릖', 'NZ': '游游', 'JP': '游游',
    'CN': '游뻟릖', 'IN': '游쉻릖', 'RU': '游游', 'OT': '游깴'
  };
  return banderas[codigoPais] || '游깴';
};

interface VotosListProps {
  votos: Voto[];
}

const VotosList: React.FC<VotosListProps> = ({ votos }) => {
  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('es-ES', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (votos.length === 0) {
    return (
      <div style={{ textAlign: 'center', padding: '40px' }}>
        <div style={{ fontSize: '4rem', marginBottom: '20px' }}>游꺔</div>
        <h3>춰S칠 el primero en votar!</h3>
        <p style={{ color: '#666' }}>
          A칰n no hay predicciones. 춰Agrega la tuya y ve c칩mo crece el 치rbol!
        </p>
      </div>
    );
  }

  return (
    <div className="votos-list">
      {votos.map((voto, index) => (
        <motion.div
          key={voto.id}
          className={`voto-card ${voto.prediccion}`}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: index * 0.1, duration: 0.5 }}
          whileHover={{ scale: 1.02 }}
        >
          <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
            {voto.foto_url && (
              <img
                src={`http://192.168.18.49:5000${voto.foto_url}`}
                alt={`Foto de ${voto.nombre}`}
                className="voto-foto"
                onError={(e) => {
                  const target = e.target as HTMLImageElement;
                  target.style.display = 'none';
                }}
              />
            )}

            {voto.pais && (
              <div style={{ fontSize: '2rem' }} title={`Pa칤s: ${voto.pais}`}>
                {getBandera(voto.pais)}
              </div>
            )}
          </div>

          <div className="voto-nombre">{voto.nombre}</div>

          <div className={`voto-prediccion ${voto.prediccion}`}>
            {voto.prediccion === 'varon' ? '游녽 Ni침o' : '游녾 Ni침a'}
          </div>

          {voto.comentario && (
            <div className="voto-comentario">
              "{voto.comentario}"
            </div>
          )}

          <div className="voto-fecha">
            {formatDate(voto.fecha_voto)}
          </div>
        </motion.div>
      ))}
    </div>
  );
};

export default VotosList;
