import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import { motion } from 'framer-motion';

interface Voto {
  id: number;
  nombre: string;
  prediccion: 'varon' | 'nina';
  comentario?: string;
  foto_url?: string;
  fecha_voto: string;
}

interface VotoFormProps {
  onSubmit: (voto: Voto) => void;
}

const VotoForm: React.FC<VotoFormProps> = ({ onSubmit }) => {
  const [nombre, setNombre] = useState('');
  const [prediccion, setPrediccion] = useState<'varon' | 'nina' | ''>('');
  const [comentario, setComentario] = useState('');
  const [foto, setFoto] = useState<File | null>(null);
  const [fotoPreview, setFotoPreview] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const onDrop = useCallback((acceptedFiles: File[]) => {
    const file = acceptedFiles[0];
    if (file) {
      setFoto(file);
      const reader = new FileReader();
      reader.onload = () => {
        setFotoPreview(reader.result as string);
      };
      reader.readAsDataURL(file);
    }
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif']
    },
    multiple: false
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!nombre.trim() || !prediccion) {
      setError('Por favor completa todos los campos obligatorios');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const formData = new FormData();
      formData.append('nombre', nombre.trim());
      formData.append('prediccion', prediccion);
      formData.append('comentario', comentario.trim());
      if (foto) {
        formData.append('foto', foto);
      }

      const response = await axios.post('http://192.168.18.49:5000/api/votar', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      onSubmit(response.data);

      // Limpiar formulario
      setNombre('');
      setPrediccion('');
      setComentario('');
      setFoto(null);
      setFotoPreview(null);

    } catch (error: any) {
      setError(error.response?.data?.error || 'Error al enviar el voto');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {error && (
        <motion.div
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          style={{
            background: '#fee2e2',
            color: '#dc2626',
            padding: '15px',
            borderRadius: '10px',
            marginBottom: '20px',
            border: '1px solid #fecaca'
          }}
        >
          {error}
        </motion.div>
      )}

      <div className="form-group">
        <label htmlFor="nombre">Tu Nombre *</label>
        <input
          type="text"
          id="nombre"
          className="input"
          value={nombre}
          onChange={(e) => setNombre(e.target.value)}
          placeholder="Escribe tu nombre completo"
          required
        />
      </div>

      <div className="form-group">
        <label>Tu Predicción *</label>
        <div className="radio-group">
          <label className="radio-option">
            <input
              type="radio"
              name="prediccion"
              value="varon"
              checked={prediccion === 'varon'}
              onChange={(e) => setPrediccion(e.target.value as 'varon' | 'nina')}
            />
            <span>👦 Varón</span>
          </label>
          <label className="radio-option">
            <input
              type="radio"
              name="prediccion"
              value="nina"
              checked={prediccion === 'nina'}
              onChange={(e) => setPrediccion(e.target.value as 'varon' | 'nina')}
            />
            <span>👧 Niña</span>
          </label>
        </div>
      </div>

      <div className="form-group">
        <label htmlFor="comentario">Comentario (opcional)</label>
        <textarea
          id="comentario"
          className="textarea"
          value={comentario}
          onChange={(e) => setComentario(e.target.value)}
          placeholder="Escribe un mensaje especial o tu razón para esta predicción..."
        />
      </div>

      <div className="form-group">
        <label>Tu Foto (opcional)</label>
        <div
          {...getRootProps()}
          className={`dropzone ${isDragActive ? 'dragover' : ''}`}
        >
          <input {...getInputProps()} />
          {fotoPreview ? (
            <div>
              <img 
                src={fotoPreview} 
                alt="Preview" 
                style={{ 
                  width: '100px', 
                  height: '100px', 
                  objectFit: 'cover', 
                  borderRadius: '50%',
                  marginBottom: '10px'
                }} 
              />
              <p>Foto seleccionada ✓</p>
              <p style={{ fontSize: '0.9rem', color: '#666' }}>
                Haz clic o arrastra otra foto para cambiar
              </p>
            </div>
          ) : (
            <div>
              <div style={{ fontSize: '3rem', marginBottom: '10px' }}>📷</div>
              {isDragActive ? (
                <p>Suelta la foto aquí...</p>
              ) : (
                <div>
                  <p><strong>Haz clic o arrastra una foto aquí</strong></p>
                  <p style={{ fontSize: '0.9rem', color: '#666' }}>
                    PNG, JPG, GIF hasta 5MB
                  </p>
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      <motion.button
        type="submit"
        className="btn"
        disabled={loading}
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        style={{ width: '100%', marginTop: '20px' }}
      >
        {loading ? 'Enviando voto...' : '🌳 Agregar mi predicción al árbol'}
      </motion.button>
    </form>
  );
};

export default VotoForm;
