import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import { motion } from 'framer-motion';

interface Voto {
  id: number;
  nombre: string;
  prediccion: 'varon' | 'nina';
  pais?: string;
  comentario?: string;
  foto_url?: string;
  fecha_voto: string;
}

interface VotoFormProps {
  onSubmit: (voto: Voto) => void;
}

const VotoForm: React.FC<VotoFormProps> = ({ onSubmit }) => {
  const [nombre, setNombre] = useState('');
  const [prediccion, setPrediccion] = useState<'varon' | 'nina' | ''>('');
  const [pais, setPais] = useState('VE');
  const [comentario, setComentario] = useState('');
  const [foto, setFoto] = useState<File | null>(null);
  const [fotoPreview, setFotoPreview] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Lista de países con banderas
  const paises = [
    { codigo: 'AR', nombre: 'Argentina', bandera: '🇦🇷' },
    { codigo: 'BO', nombre: 'Bolivia', bandera: '🇧🇴' },
    { codigo: 'BR', nombre: 'Brasil', bandera: '🇧🇷' },
    { codigo: 'CL', nombre: 'Chile', bandera: '🇨🇱' },
    { codigo: 'CO', nombre: 'Colombia', bandera: '🇨🇴' },
    { codigo: 'CR', nombre: 'Costa Rica', bandera: '🇨🇷' },
    { codigo: 'CU', nombre: 'Cuba', bandera: '🇨🇺' },
    { codigo: 'DO', nombre: 'República Dominicana', bandera: '🇩🇴' },
    { codigo: 'EC', nombre: 'Ecuador', bandera: '🇪🇨' },
    { codigo: 'SV', nombre: 'El Salvador', bandera: '🇸🇻' },
    { codigo: 'ES', nombre: 'España', bandera: '🇪🇸' },
    { codigo: 'GT', nombre: 'Guatemala', bandera: '🇬🇹' },
    { codigo: 'HN', nombre: 'Honduras', bandera: '🇭🇳' },
    { codigo: 'MX', nombre: 'México', bandera: '🇲🇽' },
    { codigo: 'NI', nombre: 'Nicaragua', bandera: '🇳🇮' },
    { codigo: 'PA', nombre: 'Panamá', bandera: '🇵🇦' },
    { codigo: 'PY', nombre: 'Paraguay', bandera: '🇵🇾' },
    { codigo: 'PE', nombre: 'Perú', bandera: '🇵🇪' },
    { codigo: 'PR', nombre: 'Puerto Rico', bandera: '🇵🇷' },
    { codigo: 'UY', nombre: 'Uruguay', bandera: '🇺🇾' },
    { codigo: 'VE', nombre: 'Venezuela', bandera: '🇻🇪' },
    { codigo: 'US', nombre: 'Estados Unidos', bandera: '🇺🇸' },
    { codigo: 'CA', nombre: 'Canadá', bandera: '🇨🇦' },
    { codigo: 'FR', nombre: 'Francia', bandera: '🇫🇷' },
    { codigo: 'DE', nombre: 'Alemania', bandera: '🇩🇪' },
    { codigo: 'IT', nombre: 'Italia', bandera: '🇮🇹' },
    { codigo: 'GB', nombre: 'Reino Unido', bandera: '🇬🇧' },
    { codigo: 'AU', nombre: 'Australia', bandera: '🇦🇺' },
    { codigo: 'NZ', nombre: 'Nueva Zelanda', bandera: '🇳🇿' },
    { codigo: 'JP', nombre: 'Japón', bandera: '🇯🇵' },
    { codigo: 'CN', nombre: 'China', bandera: '🇨🇳' },
    { codigo: 'IN', nombre: 'India', bandera: '🇮🇳' },
    { codigo: 'RU', nombre: 'Rusia', bandera: '🇷🇺' },
    { codigo: 'OT', nombre: 'Otro', bandera: '🌍' }
  ];

  const onDrop = useCallback((acceptedFiles: File[]) => {
    const file = acceptedFiles[0];
    if (file) {
      setFoto(file);
      const reader = new FileReader();
      reader.onload = () => {
        setFotoPreview(reader.result as string);
      };
      reader.readAsDataURL(file);
    }
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif']
    },
    multiple: false
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!nombre.trim() || !prediccion) {
      setError('Por favor completa todos los campos obligatorios');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const formData = new FormData();
      formData.append('nombre', nombre.trim());
      formData.append('prediccion', prediccion);
      formData.append('pais', pais);
      formData.append('comentario', comentario.trim());
      if (foto) {
        formData.append('foto', foto);
      }

      const response = await axios.post('http://192.168.18.49:5000/api/votar', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      onSubmit(response.data);

      // Limpiar formulario
      setNombre('');
      setPrediccion('');
      setPais('');
      setComentario('');
      setFoto(null);
      setFotoPreview(null);

    } catch (error: any) {
      setError(error.response?.data?.error || 'Error al enviar el voto');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {error && (
        <motion.div
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          style={{
            background: '#fee2e2',
            color: '#dc2626',
            padding: '15px',
            borderRadius: '10px',
            marginBottom: '20px',
            border: '1px solid #fecaca'
          }}
        >
          {error}
        </motion.div>
      )}

      <div className="form-group">
        <label>Tu Predicción *</label>
        <div className="radio-group">
          <label className="radio-option">
            <input
              type="radio"
              name="prediccion"
              value="varon"
              checked={prediccion === 'varon'}
              onChange={(e) => setPrediccion(e.target.value as 'varon' | 'nina')}
            />
            <span>👦 Niño</span>
          </label>
          <label className="radio-option">
            <input
              type="radio"
              name="prediccion"
              value="nina"
              checked={prediccion === 'nina'}
              onChange={(e) => setPrediccion(e.target.value as 'varon' | 'nina')}
            />
            <span>👧 Niña</span>
          </label>
        </div>
      </div>

      <div className="form-group">
        <label>Nombre y País *</label>
        <div style={{ display: 'flex', gap: '15px', alignItems: 'flex-end' }}>
          <div style={{ flex: 2 }}>
            <input
              type="text"
              id="nombre"
              className="input"
              value={nombre}
              onChange={(e) => setNombre(e.target.value)}
              placeholder="Escribe tu nombre completo"
              required
            />
          </div>
          <div style={{ flex: 1 }}>
            <select
              id="pais"
              className="input"
              value={pais}
              onChange={(e) => setPais(e.target.value)}
              required
            >
              {paises.map((paisOption) => (
                <option key={paisOption.codigo} value={paisOption.codigo}>
                  {paisOption.bandera} {paisOption.nombre}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>

      <div className="form-group">
        <label>Comentario y Foto (opcional)</label>
        <div style={{ display: 'flex', gap: '15px', alignItems: 'flex-start' }}>
          <div style={{ flex: 2 }}>
            <textarea
              id="comentario"
              className="textarea"
              value={comentario}
              onChange={(e) => setComentario(e.target.value)}
              placeholder="Escribe un mensaje especial o tu razón para esta predicción..."
              style={{ minHeight: '120px' }}
            />
          </div>
          <div style={{ flex: 1 }}>
            <div
              {...getRootProps()}
              className={`dropzone ${isDragActive ? 'dragover' : ''}`}
              style={{ minHeight: '120px', display: 'flex', alignItems: 'center', justifyContent: 'center' }}
            >
              <input {...getInputProps()} />
              {fotoPreview ? (
                <div style={{ textAlign: 'center' }}>
                  <img 
                    src={fotoPreview} 
                    alt="Preview" 
                    style={{ 
                      width: '80px', 
                      height: '80px', 
                      objectFit: 'cover', 
                      borderRadius: '50%',
                      marginBottom: '8px'
                    }} 
                  />
                  <p style={{ fontSize: '0.8rem', margin: '0' }}>Foto ✓</p>
                </div>
              ) : (
                <div style={{ textAlign: 'center' }}>
                  <div style={{ fontSize: '2rem', marginBottom: '8px' }}>📷</div>
                  {isDragActive ? (
                    <p style={{ fontSize: '0.8rem', margin: '0' }}>Suelta aquí...</p>
                  ) : (
                    <div>
                      <p style={{ fontSize: '0.8rem', margin: '0 0 4px 0' }}><strong>Foto</strong></p>
                      <p style={{ fontSize: '0.7rem', color: '#666', margin: '0' }}>
                        PNG, JPG, GIF
                      </p>
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      <motion.button
        type="submit"
        className="btn"
        disabled={loading}
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        style={{ width: '100%', marginTop: '20px' }}
      >
        {loading ? 'Enviando voto...' : '🌳 Agregar mi predicción al árbol'}
      </motion.button>
    </form>
  );
};

export default VotoForm;
