import React, { useEffect, useRef } from 'react';

interface Voto {
  id: number;
  nombre: string;
  prediccion: 'varon' | 'nina';
  comentario?: string;
  foto_url?: string;
  fecha_voto: string;
  leaf_x?: number;
  leaf_y?: number;
  leaf_size?: number;
  leaf_angle?: number;
}

interface TreeVisualizationProps {
  votos: Voto[];
}

const TreeVisualization: React.FC<TreeVisualizationProps> = ({ votos }) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);

  // Funci칩n para dibujar hoja SVG
  const drawLeaf = (ctx: CanvasRenderingContext2D, x: number, y: number, size: number, angle: number, color: string) => {
    ctx.save();
    ctx.translate(x, y);
    ctx.rotate(angle);
    ctx.scale(size / 76, size / 76); // Escalar basado en el viewBox del SVG

    // Dibujar la forma de la hoja usando el path del SVG
    ctx.fillStyle = color;
    ctx.beginPath();

    // Path del SVG convertido a coordenadas de canvas
    ctx.moveTo(57, 22.1667);
    ctx.bezierCurveTo(49.0833, 28.5, 52.25, 31.6667, 50.6667, 36.4167);
    ctx.bezierCurveTo(49.0833, 41.1667, 50.6667, 42.75, 44.3333, 47.5);
    ctx.bezierCurveTo(36.2227, 53.583, 29.2917, 49.875, 29.2917, 49.875);
    ctx.bezierCurveTo(27.7083, 51.4583, 21.7708, 54.2292, 21.7708, 54.2292);
    ctx.lineTo(20.0143, 50.7285);
    ctx.bezierCurveTo(26.3476, 49.1452, 27.7083, 47.1042, 27.7083, 47.1042);
    ctx.bezierCurveTo(26.125, 43.9375, 23.75, 38, 28.5, 30.0833);
    ctx.bezierCurveTo(33.25, 22.1667, 57, 22.1667, 57, 22.1667);
    ctx.closePath();
    ctx.fill();

    // Agregar sombra interna para dar profundidad
    ctx.fillStyle = 'rgba(0, 0, 0, 0.1)';
    ctx.beginPath();
    ctx.moveTo(30.0833, 44.3333);
    ctx.lineTo(31.6667, 45.9167);
    ctx.lineTo(31.6667, 45.9167);
    ctx.bezierCurveTo(34.8333, 44.3333, 36.4167, 39.5833, 36.4167, 39.5833);
    ctx.bezierCurveTo(38, 34.8333, 45.9167, 26.9167, 45.9167, 26.9167);
    ctx.closePath();
    ctx.fill();

    ctx.restore();
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const isMobile = window.innerWidth <= 768;

    // Configurar canvas
    canvas.width = canvas.offsetWidth;
    canvas.height = canvas.offsetHeight;
    const width = canvas.width;
    const height = canvas.height;

    // Limpiar canvas
    ctx.clearRect(0, 0, width, height);

    // Dibujar cielo con gradiente m치s suave
    const skyGradient = ctx.createLinearGradient(0, 0, 0, height * 0.7);
    skyGradient.addColorStop(0, '#87CEEB');
    skyGradient.addColorStop(0.5, '#B0E0E6');
    skyGradient.addColorStop(1, '#98FB98');
    ctx.fillStyle = skyGradient;
    ctx.fillRect(0, 0, width, height);

    // Dibujar suelo
    const groundGradient = ctx.createLinearGradient(0, height * 0.7, 0, height);
    groundGradient.addColorStop(0, '#98FB98');
    groundGradient.addColorStop(1, '#228B22');
    ctx.fillStyle = groundGradient;
    ctx.fillRect(0, height * 0.7, width, height * 0.3);

    // Cargar y dibujar im치genes de nubes decorativas (detr치s del 치rbol)
    const nube1Image = new Image();
    const nube2Image = new Image();

    let nubesLoaded = 0;

    const drawClouds = () => {
      // Nube izquierda - ampliada y manteniendo proporci칩n original
      const nube1Width = isMobile ? 140 : 300;
      const nube1Height = isMobile ? 80 : 120; // Mantener proporci칩n original
      const nube1X = width * 0.03;
      const nube1Y = height * 0.03;
      ctx.drawImage(nube1Image, nube1X, nube1Y, nube1Width, nube1Height);

      // Nube derecha - ampliada y manteniendo proporci칩n original
      const nube2Width = isMobile ? 120 : 300;
      const nube2Height = isMobile ? 70 : 120; // Mantener proporci칩n original
      const nube2X = width * 0.70;
      const nube2Y = height * 0.10;
      ctx.drawImage(nube2Image, nube2X, nube2Y, nube2Width, nube2Height);
    };

    const checkAndDrawClouds = () => {
      nubesLoaded++;
      if (nubesLoaded === 2) {
        drawClouds();
      }
    };

    nube1Image.onload = checkAndDrawClouds;
    nube2Image.onload = checkAndDrawClouds;

    nube1Image.onerror = () => {
      console.log('Error cargando nube1.png');
      nubesLoaded++;
    };

    nube2Image.onerror = () => {
      console.log('Error cargando nube2.png');
      nubesLoaded++;
    };

    nube1Image.src = '/nube1.png';
    nube2Image.src = '/nube2.png';

    // Cargar y dibujar la imagen del 치rbol
    const treeImage = new Image();
    treeImage.onload = () => {
      // Calcular dimensiones del 치rbol centrado (aumentado 35%)
      const baseSizeTree = isMobile ? 200 : 300;
      const treeSize = baseSizeTree * 1.35; // Aumentar 35%
      const treeX = (width / 2) - (treeSize / 2);
      // Posicionar la imagen del 치rbol hasta el borde inferior
      const treeY = height - treeSize;

      // Dibujar la imagen del 치rb0ol
      ctx.drawImage(treeImage, width*0.3, height*0.25  , 298, 468);

      // Despu칠s de dibujar el 치rbol, dibujar las hojas
      drawLeavesAfterTree();
    };

    // Funci칩n para dibujar las hojas despu칠s de que se carga el 치rbol
    const drawLeavesAfterTree = () => {
      // Dibujar hojas usando las posiciones guardadas en la base de datos
      votos.forEach((voto, index) => {
        // Si el voto tiene posiciones guardadas, usarlas
        if (voto.leaf_x !== undefined && voto.leaf_y !== undefined && 
            voto.leaf_size !== undefined && voto.leaf_angle !== undefined) {

          const leafX = voto.leaf_x;
          const leafY = voto.leaf_y;
          const leafSize = voto.leaf_size;
          const leafAngle = voto.leaf_angle;
          const leafColor = voto.prediccion === 'varon' ? '#1E40AF' : '#BE185D';

          drawLeaf(ctx, leafX, leafY, leafSize, leafAngle, leafColor);
        } else {
          // Fallback: calcular posici칩n si no est치 guardada (para votos existentes)
          const treeCenterX = width / 2;
          const baseTreeCenterY = isMobile ? height - 100 : height - 150;
          const treeCenterY = baseTreeCenterY - (baseTreeCenterY * 0.3);

                     const angle = index * 2.4;
           const baseRadius = isMobile ? (40 + Math.sqrt(index) * 6) : (80 + Math.sqrt(index) * 14);
           const radius = baseRadius * 0.7;

           // Usar funciones determin칤sticas basadas en el 칤ndice para consistencia (reducida)
           const randomAngle = (Math.sin(index * 12.9898 + 78.233) * 43758.5453 % 1 - 0.5) * 0.3;
           const randomRadius = (Math.sin(index * 92.9898 + 78.233) * 43758.5453 % 1 - 0.5) * 15;

          const leafX = treeCenterX + Math.cos(angle + randomAngle) * (radius + randomRadius);
          const leafY = treeCenterY + Math.sin(angle + randomAngle) * (radius + randomRadius) * 0.6;

          const baseLeafSize = 87 + (Math.sin(index * 67.233 + 45.678) * 43758.5453 % 1) * 38;
          const leafSize = baseLeafSize * 0.95;
          const leafAngle = (Math.sin(index * 34.567 + 89.123) * 43758.5453 % 1) * Math.PI * 2;
          const leafColor = voto.prediccion === 'varon' ? '#1E40AF' : '#BE185D';

          drawLeaf(ctx, leafX, leafY, leafSize, leafAngle, leafColor);
        }
      });
    };

    // Intentar cargar la imagen del 치rbol, si no existe usar el 치rbol dibujado
    treeImage.onerror = () => {
      // Si no se puede cargar la imagen, dibujar el 치rbol original
      drawOriginalTree();
      drawLeavesAfterTree();
    };

    treeImage.src = '/arbol.png';

    // Funci칩n para dibujar el 치rbol original si no hay imagen
    const drawOriginalTree = () => {
      // Dibujar tronco del 치rbol con gradiente hasta el borde inferior
      const trunkGradient = ctx.createLinearGradient(width / 2 - 25, height - 280, width / 2 + 25, height - 280);
      trunkGradient.addColorStop(0, '#8B4513');
      trunkGradient.addColorStop(0.5, '#A0522D');
      trunkGradient.addColorStop(1, '#8B4513');
      ctx.fillStyle = trunkGradient;
      // Extender el tronco hasta el borde inferior exacto
      const trunkHeight = isMobile ? 400 : 500;
      const trunkY = height - trunkHeight;
      ctx.fillRect(width / 2 - 25, trunkY, 50, trunkHeight);

      // Dibujar ramas principales con mejor distribuci칩n
      ctx.strokeStyle = '#654321';
      ctx.lineWidth = 10;
      ctx.lineCap = 'round';

      // Posiciones base para las ramas (ajustadas para tronco hasta el borde)
      const branchBaseY = isMobile ? (height - 350) : (height - 450);
      const branchMidY = isMobile ? (height - 380) : (height - 480);
      const branchEndY = isMobile ? (height - 400) : (height - 500);
      const branchSecY = isMobile ? (height - 370) : (height - 470);
      const branchSecMidY = isMobile ? (height - 390) : (height - 490);
      const branchSecEndY = isMobile ? (height - 410) : (height - 510);
      const branchTopY = isMobile ? (height - 360) : (height - 460);
      const branchTopMidY = isMobile ? (height - 400) : (height - 500);
      const branchTopEndY = isMobile ? (height - 440) : (height - 540);

      // Rama izquierda principal
      ctx.beginPath();
      ctx.moveTo(width / 2 - 15, branchBaseY);
      ctx.quadraticCurveTo(width / 2 - 60, branchMidY, width / 2 - 100, branchEndY);
      ctx.stroke();

      // Rama derecha principal
      ctx.beginPath();
      ctx.moveTo(width / 2 + 15, branchBaseY);
      ctx.quadraticCurveTo(width / 2 + 60, branchMidY, width / 2 + 100, branchEndY);
      ctx.stroke();

      // Ramas secundarias izquierdas
      ctx.lineWidth = 6;
      ctx.beginPath();
      ctx.moveTo(width / 2 - 8, branchSecY);
      ctx.quadraticCurveTo(width / 2 - 40, branchSecMidY, width / 2 - 70, branchSecEndY);
      ctx.stroke();

      // Ramas secundarias derechas
      ctx.beginPath();
      ctx.moveTo(width / 2 + 8, branchSecY);
      ctx.quadraticCurveTo(width / 2 + 40, branchSecMidY, width / 2 + 70, branchSecEndY);
      ctx.stroke();

      // Ramas superiores
      ctx.lineWidth = 4;
      ctx.beginPath();
      ctx.moveTo(width / 2, branchTopY);
      ctx.quadraticCurveTo(width / 2 - 30, branchTopMidY, width / 2 - 50, branchTopEndY);
      ctx.stroke();

      ctx.beginPath();
      ctx.moveTo(width / 2, branchTopY);
      ctx.quadraticCurveTo(width / 2 + 30, branchTopMidY, width / 2 + 50, branchTopEndY);
      ctx.stroke();
    };

  }, [votos]);

  return (
    <div style={{ width: '100%', height: '625px', border: '2px solid #ddd', borderRadius: '10px', overflow: 'hidden' }}>
      <canvas
        ref={canvasRef}
        style={{ width: '100%', height: '100%', display: 'block' }}
      />

      {/* Leyenda */}
      <div style={{
        position: 'absolute',
        bottom: '20px',
        left: '20px',
        background: 'rgba(255, 255, 255, 0.9)',
        padding: '15px',
        borderRadius: '10px',
        backdropFilter: 'blur(10px)',
        border: '1px solid rgba(255, 255, 255, 0.3)'
      }}>
        {(!window.innerWidth || window.innerWidth > 768) && (
          <h4 style={{ margin: '0 0 10px 0', color: '#333', fontSize: '1rem' }}>Leyenda</h4>
        )}
        <div style={{ display: 'flex', flexDirection: window.innerWidth <= 768 ? 'row' : 'column', gap: '8px', alignItems: 'center' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
            <div style={{ width: '20px', height: '15px', backgroundColor: '#1E40AF', borderRadius: '50%' }}></div>
            <span style={{ fontSize: '0.9rem', color: '#333' }}>Ni침o</span>
          </div>
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
            <div style={{ width: '20px', height: '15px', backgroundColor: '#BE185D', borderRadius: '50%' }}></div>
            <span style={{ fontSize: '0.9rem', color: '#333' }}>Ni침a</span>
          </div>
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginTop: window.innerWidth <= 768 ? '0' : '5px' }}>
            <span style={{ fontSize: '1.2rem' }}>游</span>
            <span style={{ fontSize: '0.9rem', color: '#333' }}>{votos.length}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TreeVisualization;
