import React, { useRef } from 'react';
import { Document, Page, Text, View, StyleSheet, pdf, Image, Font } from '@react-pdf/renderer';
import { motion } from 'framer-motion';

interface PDFGeneratorProps {
  votos: any[];
  estadisticas: any;
  treeRef: React.RefObject<HTMLDivElement>;
}

// Registrar fuentes para mejor soporte de caracteres
Font.register({
  family: 'Inter',
  src: 'https://fonts.gstatic.com/s/inter/v12/UcCO3FwrK3iLTeHuS_fvQtMwCp50KnMw2boKoduKmMEVuLyfAZ9hiA.woff2'
});

// Estilos modernos
const styles = StyleSheet.create({
  page: {
    flexDirection: 'column',
    backgroundColor: '#F8FAFC',
    padding: 30,
    fontFamily: 'Inter'
  },
  header: {
    textAlign: 'center',
    marginBottom: 30
  },
  title: {
    fontSize: 28,
    fontWeight: 'bold',
    color: '#1F2937',
    marginBottom: 10
  },
  subtitle: {
    fontSize: 16,
    color: '#6B7280',
    marginBottom: 20
  },
  treeContainer: {
    alignItems: 'center',
    marginBottom: 40
  },
  treeImage: {
    width: 300,
    height: 200,
    objectFit: 'contain'
  },
  statsSection: {
    marginBottom: 40
  },
  statsTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#1F2937',
    textAlign: 'center',
    marginBottom: 20
  },
  statsGrid: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    marginBottom: 20
  },
  statCard: {
    backgroundColor: '#FFFFFF',
    borderRadius: 12,
    padding: 20,
    width: 120,
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3
  },
  statNumber: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 5
  },
  statLabel: {
    fontSize: 12,
    color: '#6B7280'
  },
  percentageCircle: {
    width: 30,
    height: 30,
    borderRadius: 15,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 10
  },
  percentageText: {
    fontSize: 10,
    fontWeight: 'bold'
  },
  messagesSection: {
    marginBottom: 40
  },
  messagesTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#1F2937',
    textAlign: 'center',
    marginBottom: 20
  },
  messagesGrid: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-between'
  },
  messageCard: {
    backgroundColor: '#FFFFFF',
    borderRadius: 12,
    padding: 15,
    width: '30%',
    marginBottom: 15,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3
  },
  cardHeader: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10
  },
  profileImage: {
    width: 30,
    height: 30,
    borderRadius: 15,
    marginRight: 10
  },
  nameContainer: {
    flex: 1
  },
  name: {
    fontSize: 12,
    fontWeight: 'bold',
    color: '#1F2937',
    marginBottom: 2
  },
  countryFlag: {
    fontSize: 14,
    marginRight: 5
  },
  genderTag: {
    paddingHorizontal: 8,
    paddingVertical: 2,
    borderRadius: 10,
    marginBottom: 8
  },
  genderText: {
    fontSize: 10,
    fontWeight: 'bold'
  },
  message: {
    fontSize: 10,
    color: '#374151',
    textAlign: 'center',
    marginBottom: 8,
    fontStyle: 'italic'
  },
  date: {
    fontSize: 8,
    color: '#9CA3AF',
    textAlign: 'left'
  },
  finalMessage: {
    backgroundColor: '#FFFFFF',
    borderRadius: 16,
    padding: 30,
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 4 },
    shadowOpacity: 0.15,
    shadowRadius: 8,
    elevation: 5
  },
  finalTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#1F2937',
    marginBottom: 20,
    textAlign: 'center'
  },
  finalText: {
    fontSize: 14,
    color: '#4B5563',
    textAlign: 'center',
    lineHeight: 1.6,
    marginBottom: 10
  },
  signature: {
    fontSize: 12,
    color: '#667EEA',
    fontStyle: 'italic',
    marginTop: 20
  }
});

// Componente para las tarjetas de mensajes
const MessageCard = ({ voto, index }: { voto: any; index: number }) => {
  // Banderas de pa√≠ses
  const banderas: { [key: string]: string } = {
    'AR': 'üá¶üá∑', 'BO': 'üáßüá¥', 'BR': 'üáßüá∑', 'CL': 'üá®üá±', 'CO': 'üá®üá¥', 'CR': 'üá®üá∑', 'CU': 'üá®üá∫', 'DO': 'üá©üá¥', 'EC': 'üá™üá®', 'SV': 'üá∏üáª',
    'ES': 'üá™üá∏', 'GT': 'üá¨üáπ', 'HN': 'üá≠üá≥', 'MX': 'üá≤üáΩ', 'NI': 'üá≥üáÆ', 'PA': 'üáµüá¶', 'PY': 'üáµüáæ', 'PE': 'üáµüá™', 'PR': 'üáµüá∑', 'UY': 'üá∫üáæ',
    'VE': 'üáªüá™', 'US': 'üá∫üá∏', 'CA': 'üá®üá¶', 'FR': 'üá´üá∑', 'DE': 'üá©üá™', 'IT': 'üáÆüáπ', 'GB': 'üá¨üáß', 'AU': 'üá¶üá∫', 'NZ': 'üá≥üáø', 'JP': 'üáØüáµ',
    'CN': 'üá®üá≥', 'IN': 'üáÆüá≥', 'RU': 'üá∑üá∫', 'OT': 'üåç'
  };

  const bandera = banderas[voto.pais] || 'üåç';
  const nombreLimpio = voto.nombre?.toString().trim() || 'An√≥nimo';
  const comentarioLimpio = voto.comentario?.toString().trim() || 'Sin comentario';

  // Formatear fecha
  let fechaTexto = 'Fecha no disponible';
  try {
    if (voto.fecha) {
      const fecha = new Date(voto.fecha);
      if (!isNaN(fecha.getTime())) {
        fechaTexto = fecha.toLocaleDateString('es-ES', {
          day: 'numeric',
          month: 'short',
          year: 'numeric'
        });
      }
    }
  } catch (error) {
    console.log('Error procesando fecha:', voto.fecha);
  }

  const isNino = voto.prediccion === 'varon';
  const genderTagStyle = {
    backgroundColor: isNino ? '#D4F0F7' : '#FADADD'
  };
  const genderTextStyle = {
    color: isNino ? '#1E40AF' : '#BE185D'
  };

  return (
    <View style={styles.messageCard}>
      <View style={styles.cardHeader}>
        {voto.foto_url ? (
          <Image 
            src={`http://192.168.18.49:5000${voto.foto_url}`}
            style={styles.profileImage}
          />
        ) : (
          <View style={[styles.profileImage, { backgroundColor: '#E5E7EB' }]} />
        )}
        <View style={styles.nameContainer}>
          <Text style={styles.name}>
            <Text style={styles.countryFlag}>{bandera}</Text>
            {nombreLimpio}
          </Text>
        </View>
      </View>

      <View style={[styles.genderTag, genderTagStyle]}>
        <Text style={[styles.genderText, genderTextStyle]}>
          {isNino ? 'üë¶ Ni√±o' : 'üëß Ni√±a'}
        </Text>
      </View>

      <Text style={styles.message}>"{comentarioLimpio}"</Text>
      <Text style={styles.date}>{fechaTexto}</Text>
    </View>
  );
};

// Componente principal del PDF
const PDFDocument = ({ votos, estadisticas, treeImageData }: { 
  votos: any[]; 
  estadisticas: any; 
  treeImageData: string;
}) => {
  const comentariosValidos = votos.filter(voto => 
    voto.comentario && 
    voto.comentario.trim().length > 0 && 
    voto.comentario.trim() !== 'undefined' &&
    voto.comentario.trim() !== 'null'
  );

  const porcentajeNino = Math.round((estadisticas.votos_varon / estadisticas.total_votos) * 100);
  const porcentajeNina = Math.round((estadisticas.votos_nina / estadisticas.total_votos) * 100);

  return (
    <Document>
      {/* P√°gina 1: Portada */}
      <Page size="A4" style={styles.page}>
        <View style={styles.header}>
          <Text style={styles.title}>üåø √Årbol de Predicciones</Text>
          <Text style={styles.subtitle}>Un Recuerdo Especial para los Futuros Padres</Text>
        </View>

        <View style={styles.treeContainer}>
          <Image src={treeImageData} style={styles.treeImage} />
        </View>

        <Text style={styles.subtitle}>
          Generado el {new Date().toLocaleDateString('es-ES', {
            year: 'numeric',
            month: 'long',
            day: 'numeric'
          })}
        </Text>
      </Page>

      {/* P√°gina 2: Estad√≠sticas */}
      <Page size="A4" style={styles.page}>
        <View style={styles.statsSection}>
          <Text style={styles.statsTitle}>üìä Estad√≠sticas de las Predicciones</Text>

          <View style={styles.statsGrid}>
            <View style={styles.statCard}>
              <Text style={[styles.statNumber, { color: '#667EEA' }]}>
                {estadisticas.total_votos}
              </Text>
              <Text style={styles.statLabel}>Total Votos</Text>
            </View>

            <View style={styles.statCard}>
              <Text style={[styles.statNumber, { color: '#1E40AF' }]}>
                {estadisticas.votos_varon}
              </Text>
              <Text style={styles.statLabel}>üë¶ Ni√±o</Text>
              <View style={[styles.percentageCircle, { backgroundColor: '#D4F0F7' }]}>
                <Text style={[styles.percentageText, { color: '#1E40AF' }]}>
                  {porcentajeNino}%
                </Text>
              </View>
            </View>

            <View style={styles.statCard}>
              <Text style={[styles.statNumber, { color: '#BE185D' }]}>
                {estadisticas.votos_nina}
              </Text>
              <Text style={styles.statLabel}>üëß Ni√±a</Text>
              <View style={[styles.percentageCircle, { backgroundColor: '#FADADD' }]}>
                <Text style={[styles.percentageText, { color: '#BE185D' }]}>
                  {porcentajeNina}%
                </Text>
              </View>
            </View>
          </View>
        </View>
      </Page>

      {/* P√°ginas de mensajes */}
      {comentariosValidos.length > 0 && (
        <Page size="A4" style={styles.page}>
          <View style={styles.messagesSection}>
            <Text style={styles.messagesTitle}>üí¨ Mensajes Especiales</Text>
            <View style={styles.messagesGrid}>
              {comentariosValidos.map((voto, index) => (
                <MessageCard key={index} voto={voto} index={index} />
              ))}
            </View>
          </View>
        </Page>
      )}

      {/* P√°gina final: Mensaje especial */}
      <Page size="A4" style={styles.page}>
        <View style={styles.finalMessage}>
          <Text style={styles.finalTitle}>üéâ ¬°Felicidades Futuros Padres!</Text>

          <Text style={styles.finalText}>
            Este √°rbol representa el amor y las buenas vibras que toda su familia y amigos tienen para ustedes.
          </Text>

          <Text style={styles.finalText}>
            Cada hoja es una predicci√≥n llena de cari√±o y esperanza para el nuevo miembro de la familia.
          </Text>

          <Text style={styles.finalText}>
            ¬°Que este beb√© traiga mucha alegr√≠a y bendiciones a sus vidas! üåü
          </Text>

          <Text style={styles.signature}>
            Con mucho amor y buenos deseos
          </Text>
        </View>
      </Page>
    </Document>
  );
};

const PDFGenerator: React.FC<PDFGeneratorProps> = ({ votos, estadisticas, treeRef }) => {
  const [generating, setGenering] = React.useState(false);

  const generatePDF = async () => {
    if (!treeRef.current) return;

    setGenering(true);

    try {
      // Capturar imagen del √°rbol
      const canvas = document.createElement('canvas');
      const context = canvas.getContext('2d');
      const treeElement = treeRef.current;

      // Configurar canvas
      canvas.width = treeElement.offsetWidth * 2;
      canvas.height = treeElement.offsetHeight * 2;
      context?.scale(2, 2);

      // Convertir el elemento a imagen
      const treeImageData = await new Promise<string>((resolve) => {
        const img = new Image();
        img.onload = () => {
          context?.drawImage(img, 0, 0);
          resolve(canvas.toDataURL('image/png'));
        };
        img.src = `data:image/svg+xml;base64,${btoa(treeElement.outerHTML)}`;
      });

      // Generar PDF
      const blob = await pdf(<PDFDocument votos={votos} estadisticas={estadisticas} treeImageData={treeImageData} />).toBlob();

      // Descargar PDF
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = 'arbol-predicciones-recuerdo-especial.pdf';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      URL.revokeObjectURL(url);

    } catch (error) {
      console.error('Error generando PDF:', error);
      alert('Error al generar el PDF. Int√©ntalo de nuevo.');
    } finally {
      setGenering(false);
    }
  };

  return (
    <motion.button
      onClick={generatePDF}
      disabled={generating}
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
      style={{
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        color: 'white',
        border: 'none',
        borderRadius: '15px',
        padding: '18px 30px',
        fontSize: '1.1rem',
        fontWeight: '600',
        cursor: generating ? 'not-allowed' : 'pointer',
        width: '100%',
        marginTop: '20px',
        boxShadow: '0 4px 15px rgba(102, 126, 234, 0.4)',
        transition: 'all 0.3s ease',
        opacity: generating ? 0.7 : 1
      }}
    >
      {generating ? (
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px' }}>
          <div style={{ 
            width: '20px', 
            height: '20px', 
            border: '2px solid rgba(255,255,255,0.3)', 
            borderTop: '2px solid white', 
            borderRadius: '50%', 
            animation: 'spin 1s linear infinite' 
          }} />
          Generando recuerdo especial...
        </div>
      ) : (
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px' }}>
          <span style={{ fontSize: '1.3rem' }}>üìÑ</span>
          <span>Crear Recuerdo Especial</span>
        </div>
      )}
    </motion.button>
  );
};

export default PDFGenerator;
