import React, { useRef } from 'react';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import { motion } from 'framer-motion';

interface PDFGeneratorProps {
  votos: any[];
  estadisticas: any;
  treeRef: React.RefObject<HTMLDivElement>;
}

const PDFGenerator: React.FC<PDFGeneratorProps> = ({ votos, estadisticas, treeRef }) => {
  const [generating, setGenering] = React.useState(false);

  // Utilidad: carga una imagen en alta resoluci칩n
  const loadImage = (src: string): Promise<HTMLImageElement> => {
    return new Promise((resolve, reject) => {
      const img = new Image();
      img.crossOrigin = 'anonymous';
      img.onload = () => resolve(img);
      img.onerror = reject;
      img.src = src;
    });
  };

  // Utilidad: crea una imagen circular de alta calidad
  const createCircularImageDataUrl = async (src: string, size: number): Promise<string> => {
    try {
      const img = await loadImage(src);
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d');

      // Usar alta resoluci칩n para evitar pixelaci칩n
      const scale = 2;
      const scaledSize = size * scale;
      canvas.width = scaledSize;
      canvas.height = scaledSize;

      if (!ctx) return '';

      // Configurar para alta calidad
      ctx.imageSmoothingEnabled = true;
      ctx.imageSmoothingQuality = 'high';

      ctx.clearRect(0, 0, scaledSize, scaledSize);
      ctx.save();
      ctx.beginPath();
      ctx.arc(scaledSize / 2, scaledSize / 2, scaledSize / 2, 0, Math.PI * 2, true);
      ctx.closePath();
      ctx.clip();

      // Calcular dimensiones para mantener aspecto y llenar el c칤rculo
      const imgAspect = img.width / img.height;
      let drawWidth = scaledSize;
      let drawHeight = scaledSize;
      let offsetX = 0;
      let offsetY = 0;

      if (imgAspect > 1) {
        // Imagen m치s ancha que alta
        drawHeight = scaledSize;
        drawWidth = drawHeight * imgAspect;
        offsetX = -(drawWidth - scaledSize) / 2;
      } else {
        // Imagen m치s alta que ancha
        drawWidth = scaledSize;
        drawHeight = drawWidth / imgAspect;
        offsetY = -(drawHeight - scaledSize) / 2;
      }

      ctx.drawImage(img, offsetX, offsetY, drawWidth, drawHeight);
      ctx.restore();

      return canvas.toDataURL('image/png');
    } catch (error) {
      console.log('Error creating circular image:', error);
      return '';
    }
  };

  // Utilidad: truncar texto que no cabe en el ancho disponible
  const truncateText = (pdf: jsPDF, text: string, maxWidth: number, fontSize: number): string => {
    pdf.setFontSize(fontSize);
    const textWidth = pdf.getTextWidth(text);

    if (textWidth <= maxWidth) {
      return text;
    }

    // Truncar y agregar "..."
    let truncated = text;
    while (pdf.getTextWidth(truncated + '...') > maxWidth && truncated.length > 1) {
      truncated = truncated.slice(0, -1);
    }

    return truncated + '...';
  };

  const generatePDF = async () => {
    if (!treeRef.current) return;

    setGenering(true);

    try {
      const pdf = new jsPDF('p', 'mm', 'a4');
      const pageWidth = pdf.internal.pageSize.getWidth();
      const pageHeight = pdf.internal.pageSize.getHeight();

      // Usar fuente Helvetica (m치s legible que la default)
      pdf.setFont('helvetica');

      // P치gina 1: Portada con encabezado
      pdf.setFillColor(248, 250, 252);
      pdf.rect(0, 0, pageWidth, pageHeight, 'F');

      // Encabezado principal con mejor tipograf칤a
      pdf.setFontSize(22);
      pdf.setTextColor(51, 51, 51);
      pdf.setFont('helvetica', 'bold');
      pdf.text('游 츼rbol de Predicciones', pageWidth / 2, 25, { align: 'center' });
      pdf.setFontSize(16);
      pdf.setTextColor(102, 102, 102);
      pdf.setFont('helvetica', 'normal');
      pdf.text('Un Recuerdo Especial', pageWidth / 2, 35, { align: 'center' });

      // Capturar imagen del 치rbol en alta calidad
      const treeCanvas = await html2canvas(treeRef.current, {
        scale: 2, // Mayor escala para mejor calidad
        useCORS: true,
        allowTaint: true,
        backgroundColor: '#ffffff',
        logging: false,
        width: treeRef.current.offsetWidth,
        height: treeRef.current.offsetHeight
      });

      const treeImgData = treeCanvas.toDataURL('image/png');
      const treeAspectRatio = treeCanvas.width / treeCanvas.height;
      const treeWidth = pageWidth - 40;
      const treeHeight = treeWidth / treeAspectRatio;

      // Agregar imagen del 치rbol centrada
      pdf.addImage(treeImgData, 'PNG', 20, 50, treeWidth, treeHeight);

      // Fecha de generaci칩n
      pdf.setFontSize(12);
      pdf.setTextColor(153, 153, 153);
      pdf.setFont('helvetica', 'normal');
      const fecha = new Date().toLocaleDateString('es-ES', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });
      pdf.text(`Generado el ${fecha}`, pageWidth / 2, pageHeight - 20, { align: 'center' });

      // P치gina 2: Estad칤sticas en tarjetas modernas
      pdf.addPage();
      pdf.setFillColor(248, 250, 252);
      pdf.rect(0, 0, pageWidth, pageHeight, 'F');

      // T칤tulo de estad칤sticas
      pdf.setFontSize(20);
      pdf.setTextColor(51, 51, 51);
      pdf.setFont('helvetica', 'bold');
      pdf.text('Estad칤sticas de las Predicciones', pageWidth / 2, 30, { align: 'center' });

      // Dibujar tarjetas de estad칤sticas modernas
      const cardWidthStats = 55;
      const cardHeightStats = 35;
      const startX = (pageWidth - (cardWidthStats * 3 + 20)) / 2;

      // Tarjeta Total
      pdf.setFillColor(255, 255, 255);
      pdf.setDrawColor(230, 230, 230);
      pdf.setLineWidth(0.5);
      pdf.roundedRect(startX, 50, cardWidthStats, cardHeightStats, 8, 8, 'FD');
      pdf.setTextColor(102, 126, 234);
      pdf.setFontSize(18);
      pdf.setFont('helvetica', 'bold');
      pdf.text(estadisticas.total_votos.toString(), startX + cardWidthStats/2, 65, { align: 'center' });
      pdf.setFontSize(10);
      pdf.setTextColor(102, 102, 102);
      pdf.setFont('helvetica', 'normal');
      pdf.text('Total Votos', startX + cardWidthStats/2, 75, { align: 'center' });

      // Tarjeta Ni침o
      pdf.setFillColor(255, 255, 255);
      pdf.roundedRect(startX + cardWidthStats + 10, 50, cardWidthStats, cardHeightStats, 8, 8, 'FD');
      pdf.setTextColor(30, 64, 175);
      pdf.setFontSize(18);
      pdf.setFont('helvetica', 'bold');
      pdf.text(estadisticas.votos_varon.toString(), startX + cardWidthStats + 10 + cardWidthStats/2, 65, { align: 'center' });
      pdf.setFontSize(10);
      pdf.setTextColor(102, 102, 102);
      pdf.setFont('helvetica', 'normal');
      pdf.text('Ni침o', startX + cardWidthStats + 10 + cardWidthStats/2, 75, { align: 'center' });

      // Tarjeta Ni침a
      pdf.setFillColor(255, 255, 255);
      pdf.roundedRect(startX + (cardWidthStats + 10) * 2, 50, cardWidthStats, cardHeightStats, 8, 8, 'FD');
      pdf.setTextColor(190, 24, 93);
      pdf.setFontSize(18);
      pdf.setFont('helvetica', 'bold');
      pdf.text(estadisticas.votos_nina.toString(), startX + (cardWidthStats + 10) * 2 + cardWidthStats/2, 65, { align: 'center' });
      pdf.setFontSize(10);
      pdf.setTextColor(102, 102, 102);
      pdf.setFont('helvetica', 'normal');
      pdf.text('Ni침a', startX + (cardWidthStats + 10) * 2 + cardWidthStats/2, 75, { align: 'center' });

      // Porcentajes en c칤rculos peque침os
      pdf.setFontSize(12);
      const porcentajeNino = Math.round((estadisticas.votos_varon / estadisticas.total_votos) * 100);
      const porcentajeNina = Math.round((estadisticas.votos_nina / estadisticas.total_votos) * 100);

      // C칤rculo para ni침o
      pdf.setFillColor(212, 240, 247);
      pdf.circle(startX + cardWidthStats + 10 + cardWidthStats/2, 90, 8, 'F');
      pdf.setTextColor(30, 64, 175);
      pdf.setFontSize(10);
      pdf.setFont('helvetica', 'bold');
      pdf.text(`${porcentajeNino}%`, startX + cardWidthStats + 10 + cardWidthStats/2, 93, { align: 'center' });

      // C칤rculo para ni침a
      pdf.setFillColor(250, 218, 221);
      pdf.circle(startX + (cardWidthStats + 10) * 2 + cardWidthStats/2, 90, 8, 'F');
      pdf.setTextColor(190, 24, 93);
      pdf.text(`${porcentajeNina}%`, startX + (cardWidthStats + 10) * 2 + cardWidthStats/2, 93, { align: 'center' });

      // Filtrar comentarios v치lidos
      const comentariosValidos = votos.filter(voto => 
        voto.comentario && 
        voto.comentario.trim().length > 0 && 
        voto.comentario.trim() !== 'undefined' &&
        voto.comentario.trim() !== 'null'
      );

      if (comentariosValidos.length > 0) {
        // P치gina 3+: Comentarios en rejilla de 3 columnas
        pdf.addPage();
        pdf.setFillColor(248, 250, 252);
        pdf.rect(0, 0, pageWidth, pageHeight, 'F');

        // T칤tulo de la p치gina
        pdf.setFontSize(20);
        pdf.setTextColor(51, 51, 51);
        pdf.setFont('helvetica', 'bold');
        pdf.text('Mensajes Especiales', pageWidth / 2, 25, { align: 'center' });

        // Configuraci칩n de la rejilla
        const marginX = 15;
        const marginY = 40;
        const cardWidth = (pageWidth - (marginX * 2) - 20) / 3;
        const cardHeight = 75;
        const cardSpacing = 10;

        let currentRow = 0;
        let currentCol = 0;
        let pageCount = 3;

        for (let index = 0; index < comentariosValidos.length; index++) {
          const voto = comentariosValidos[index];

          // Verificar si necesitamos nueva p치gina
          const currentY = marginY + (currentRow * (cardHeight + cardSpacing));
          if (currentY + cardHeight > pageHeight - 30) {
            pdf.addPage();
            pdf.setFillColor(248, 250, 252);
            pdf.rect(0, 0, pageWidth, pageHeight, 'F');
            pdf.setFontSize(20);
            pdf.setTextColor(51, 51, 51);
            pdf.setFont('helvetica', 'bold');
            pdf.text(`Mensajes Especiales (P치gina ${pageCount})`, pageWidth / 2, 25, { align: 'center' });
            currentRow = 0;
            currentCol = 0;
            pageCount++;
          }

          const cardX = marginX + (currentCol * (cardWidth + 10));
          const cardY = marginY + (currentRow * (cardHeight + cardSpacing));

          // Dibujar card con estilo moderno
          pdf.setFillColor(255, 255, 255);
          pdf.setDrawColor(230, 230, 230);
          pdf.setLineWidth(0.5);
          pdf.roundedRect(cardX, cardY, cardWidth, cardHeight, 8, 8, 'FD');

          // Agregar sombra sutil
          pdf.setDrawColor(200, 200, 200);
          pdf.setLineWidth(0.3);
          pdf.roundedRect(cardX + 1, cardY + 1, cardWidth, cardHeight, 8, 8, 'S');

          // Bandera de alta calidad si hay pa칤s
          const pais = (voto.pais || '').toString().toLowerCase();
          let nombreOffsetX = 8;
          if (pais && pais.length === 2) {
            try {
              // Usar URL de bandera de mayor resoluci칩n
              const flagUrl = `https://flagcdn.com/48x36/${pais}.png`;
              const flagImg = await loadImage(flagUrl);
              // Tama침o ligeramente mayor para mejor calidad
              pdf.addImage(flagImg, 'PNG', cardX + 8, cardY + 6, 14, 10.5);
              nombreOffsetX = 8 + 14 + 4;
            } catch (e) {
              // Ignorar si falla la bandera
            }
          }

          // Limpiar y truncar el nombre si es muy largo
          let nombreLimpio = voto.nombre?.toString().trim() || 'An칩nimo';
          if (nombreLimpio === 'undefined' || nombreLimpio === 'null') nombreLimpio = 'An칩nimo';

          // Calcular ancho disponible para el nombre
          const anchoDisponible = cardWidth - nombreOffsetX - 8;
          const nombreTruncado = truncateText(pdf, nombreLimpio, anchoDisponible, 11);

          // Nombre
          pdf.setFontSize(11);
          pdf.setTextColor(51, 51, 51);
          pdf.setFont('helvetica', 'bold');
          pdf.text(nombreTruncado, cardX + nombreOffsetX, cardY + 12);

          // Etiqueta de g칠nero
          pdf.setFontSize(9);
          pdf.setFont('helvetica', 'normal');
          if (voto.prediccion === 'varon') {
            pdf.setFillColor(212, 240, 247);
            pdf.roundedRect(cardX + 8, cardY + 20, 30, 8, 4, 4, 'F');
            pdf.setTextColor(30, 64, 175);
            pdf.text('游녽 Ni침o', cardX + 10, cardY + 25);
          } else {
            pdf.setFillColor(250, 218, 221);
            pdf.roundedRect(cardX + 8, cardY + 20, 30, 8, 4, 4, 'F');
            pdf.setTextColor(190, 24, 93);
            pdf.text('游녾 Ni침a', cardX + 10, cardY + 25);
          }

          // Comentario centrado
          let comentarioLimpio = voto.comentario || 'Sin comentario';
          comentarioLimpio = comentarioLimpio.toString().trim();
          if (comentarioLimpio === 'undefined' || comentarioLimpio === 'null') comentarioLimpio = 'Sin comentario';
          pdf.setFontSize(9);
          pdf.setTextColor(55, 65, 81);
          pdf.setFont('helvetica', 'normal');
          const comentario = `"${comentarioLimpio}"`;
          const lines = pdf.splitTextToSize(comentario, cardWidth - 20);
          const textHeight = lines.length * 4;
          const startTextY = cardY + 36 + Math.max(0, (cardHeight - 36 - textHeight - 12) / 2);
          lines.forEach((line: string, lineIndex: number) => {
            pdf.text(line, cardX + cardWidth / 2, startTextY + (lineIndex * 4), { align: 'center' });
          });

          // Fecha inferior
          pdf.setFontSize(8);
          pdf.setTextColor(156, 163, 175);
          let fechaTexto = 'Fecha no disponible';
          try {
            const raw = (voto.fecha_voto || voto.fecha || '').toString();
            if (raw) {
              const f = new Date(raw);
              if (!isNaN(f.getTime())) {
                const fechaVoto = f.toLocaleDateString('es-ES', { day: '2-digit', month: 'short', year: 'numeric' });
                const horaVoto = f.toLocaleTimeString('es-ES', { hour: '2-digit', minute: '2-digit' });
                fechaTexto = `${fechaVoto}, ${horaVoto}`;
              }
            }
          } catch {}
          pdf.text(fechaTexto, cardX + 8, cardY + cardHeight - 6);

          // Foto circular de alta calidad
          if (voto.foto_url) {
            try {
              const size = 22; // Ligeramente m치s grande
              const dataUrl = await createCircularImageDataUrl(`http://192.168.18.49:5000${voto.foto_url}`, size * 2);
              if (dataUrl) {
                const fotoX = cardX + cardWidth - size - 6;
                const fotoY = cardY + 6;
                pdf.addImage(dataUrl, 'PNG', fotoX, fotoY, size, size);
                // Borde circular
                pdf.setDrawColor(230, 230, 230);
                pdf.setLineWidth(0.5);
                pdf.circle(fotoX + size/2, fotoY + size/2, size/2, 'S');
              }
            } catch {
              // Placeholder m치s bonito
              const size = 22;
              const fotoX = cardX + cardWidth - size - 6;
              const fotoY = cardY + 6;
              pdf.setFillColor(243, 244, 246);
              pdf.circle(fotoX + size/2, fotoY + size/2, size/2, 'F');
              pdf.setDrawColor(209, 213, 219);
              pdf.setLineWidth(0.5);
              pdf.circle(fotoX + size/2, fotoY + size/2, size/2, 'S');
              // Icono de usuario
              pdf.setTextColor(156, 163, 175);
              pdf.setFontSize(8);
              pdf.text('游녻', fotoX + size/2, fotoY + size/2 + 2, { align: 'center' });
            }
          }

          // Avanzar a la siguiente posici칩n en la rejilla
          currentCol++;
          if (currentCol >= 3) {
            currentCol = 0;
            currentRow++;
          }
        }
      }

      // P치gina final: Mensaje especial destacado
      pdf.addPage();
      pdf.setFillColor(248, 250, 252);
      pdf.rect(0, 0, pageWidth, pageHeight, 'F');

      // T칤tulo principal
      pdf.setFontSize(24);
      pdf.setTextColor(51, 51, 51);
      pdf.setFont('helvetica', 'bold');
      pdf.text('춰Felicidades Futuros Padres!', pageWidth / 2, 50, { align: 'center' });

      // Card destacada para el mensaje final
      const finalCardWidth = pageWidth - 40;
      const finalCardHeight = 120;
      const finalCardX = 20;
      const finalCardY = 70;

      // Fondo de la card
      pdf.setFillColor(255, 255, 255);
      pdf.setDrawColor(230, 230, 230);
      pdf.setLineWidth(1);
      pdf.roundedRect(finalCardX, finalCardY, finalCardWidth, finalCardHeight, 12, 12, 'FD');

      // Borde decorativo
      pdf.setDrawColor(102, 126, 234);
      pdf.setLineWidth(2);
      pdf.roundedRect(finalCardX, finalCardY, finalCardWidth, finalCardHeight, 12, 12, 'S');

      // Mensaje final
      pdf.setFontSize(14);
      pdf.setTextColor(51, 51, 51);
      pdf.setFont('helvetica', 'normal');
      const mensajeFinal = [
        'Este 치rbol representa el amor y las buenas vibras que',
        'toda su familia y amigos tienen para ustedes.',
        '',
        'Cada hoja es una predicci칩n llena de cari침o y',
        'esperanza para el nuevo miembro de la familia.',
        '',
        '춰Que este beb칠 traiga mucha alegr칤a y bendiciones',
        'a sus vidas!'
      ];
      mensajeFinal.forEach((line, index) => {
        if (line.trim() !== '') {
          pdf.text(line, pageWidth / 2, finalCardY + 25 + (index * 12), { align: 'center' });
        }
      });

      // Firma
      pdf.setFontSize(12);
      pdf.setTextColor(102, 126, 234);
      pdf.setFont('helvetica', 'italic');
      pdf.text('Con mucho amor y buenos deseos', pageWidth / 2, finalCardY + finalCardHeight + 20, { align: 'center' });

      // Guardar el PDF
      pdf.save('arbol-predicciones-recuerdo-especial.pdf');

    } catch (error) {
      console.error('Error generando PDF:', error);
      alert('Error al generar el PDF. Int칠ntalo de nuevo.');
    } finally {
      setGenering(false);
    }
  };

  return (
    <motion.button
      onClick={generatePDF}
      disabled={generating}
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
      style={{
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        color: 'white',
        border: 'none',
        borderRadius: '15px',
        padding: '18px 30px',
        fontSize: '1.1rem',
        fontWeight: '600',
        cursor: generating ? 'not-allowed' : 'pointer',
        width: '100%',
        marginTop: '20px',
        boxShadow: '0 4px 15px rgba(102, 126, 234, 0.4)',
        transition: 'all 0.3s ease',
        opacity: generating ? 0.7 : 1
      }}
    >
      {generating ? (
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px' }}>
          <div style={{ 
            width: '20px', 
            height: '20px', 
            border: '2px solid rgba(255,255,255,0.3)', 
            borderTop: '2px solid white', 
            borderRadius: '50%', 
            animation: 'spin 1s linear infinite' 
          }} />
          Generando recuerdo especial...
        </div>
      ) : (
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px' }}>
          <span style={{ fontSize: '1.3rem' }}>游늯</span>
          <span>Crear Recuerdo Especial</span>
        </div>
      )}
    </motion.button>
  );
};

